import React, { useState } from "react";

const Quiz = () => {
 const [selectedOption, setSelectedOption] = useState("quiz");
 const [quizContent, setQuizContent] = useState([
  {
   question: "",
   answers: ["", "", "", ""],
   correct: "",
  },
 ]);

 const handleQuestion = (e, index) => {
  const { name, value } = e.target;
  const updatedQuestion = [...quizContent];
  updatedQuestion[index] = { ...updatedQuestion[index], [name]: value };
  setQuizContent(updatedQuestion);
 };

 const handleOptionChange = (event) => {
  setSelectedOption(event.target.value);
 };

 const handleAnswerChange = (questionIndex, answerIndex, e) => {
  const { value } = e.target;
  const updatedQuizContent = [...quizContent];

  if (!updatedQuizContent[questionIndex].answers) {
   updatedQuizContent[questionIndex].answers = [];
  }

  const updatedAnswers = [...updatedQuizContent[questionIndex].answers];
  updatedAnswers[answerIndex] = value;

  updatedQuizContent[questionIndex] = {
   ...updatedQuizContent[questionIndex],
   answers: updatedAnswers,
  };

  setQuizContent(updatedQuizContent);
 };

 const handleCorrectAnswerChange = (event, questionIndex) => {
  const correctAnswerIndex = Number(event.target.value);
  const updatedQuizContent = [...quizContent];
  const updatedAnswers = { ...updatedQuizContent[questionIndex] };

  updatedAnswers.correct = updatedAnswers.answers[correctAnswerIndex];
  updatedQuizContent[questionIndex] = updatedAnswers;

  setQuizContent(updatedQuizContent);
 };

 const handleAddQuestion = () => {
  setQuizContent((prevQuizContent) => [
   ...prevQuizContent,
   {
    question: "",
    answers: ["", "", "", ""],
    correct: "",
   },
  ]);
 };

 const handleDeleteQuestion = (questionIndex) => {
  setQuizContent((prevQuizContent) => [
   ...prevQuizContent.slice(0, questionIndex),
   ...prevQuizContent.slice(questionIndex + 1),
  ]);
 };

 return (
  <div className="container mt-4 quiz style1">
		<h5 className="mt-50">Crear Quiz:</h5>

   {/* <div className="form-group">
    <label>Selecione una opcion:</label>
    <select
     className="form-control"
     value={selectedOption}
     onChange={handleOptionChange}
    >
     <option value="video">Upload Video Link</option>
     <option value="text">Upload Text</option>
     <option value="quiz">Upload Quiz</option>
    </select>
   </div> */}

   {/* {selectedOption === "quiz" && */}
   { quizContent.map((question, questionIndex) => (
     <div key={questionIndex} className="mb-20 rounded module-shadow">
      <div className="container">
       <div className="row contact-widget module-box">
        <div className="col-md-10  rounded pt-3">
         <div className="form-group">
          <label>Ingrese la pregunta o trivia:</label>
          <textarea
           name="question"
           className="form-control"
           value={question.question}
           onChange={(e) => handleQuestion(e, questionIndex)}
           placeholder={`Pregunta...`}
          ></textarea>
         </div>

         <div className="form-group">
          <label>Escriba las posibles respuestas:</label>
          {question.answers.map((answer, answerIndex) => (
           <div key={answerIndex} className="mb-10">
            <input
             name="answer"
             type="text"
             className="form-control"
             value={answer}
             onChange={(e) => handleAnswerChange(questionIndex, answerIndex, e)}
            />
           </div>
          ))}

          <div className="form-group">
           <label>Seleccione la respuesta correcta:</label>
           <select
            className="form-control"
            value={question.answers.indexOf(question.correct)}
            onChange={(e) => handleCorrectAnswerChange(e, questionIndex)}
           >
            {question.answers.map((answer, index) => (
             <option key={index} value={index}>
              {answer}
             </option>
            ))}
           </select>
          </div>
         </div>
        </div>

        <div className="col-md-2 d-flex align-items-center justify-content-center">
         <div className="d-flex justify-content-center">
          <button
           type="button"
           onClick={() => {
            handleDeleteQuestion(questionIndex);
           }}
           className="btn btn-del-cont "
          >
           <i className="fa fa-trash"></i>
          </button>
         </div>
        </div>
       </div>
      </div>
     </div> ))}
    {/* // ))} */}

   <div className="container d-flex justify-content-center contact-widget">
    <button
     type="button"
     onClick={() => {
      handleAddQuestion();
     }}
     className="btn  btn-add btn-add-cont me-2 m-10"
    >
     Agregar Pregunta
    </button>
   </div>
  </div>
 );
};

export default Quiz;
