import React, { useState } from 'react';
import {
 ListGroup,
 ListGroupItem,
 Collapse,
 Container,
 Row,
 Col,
 Button,
} from 'react-bootstrap';

const CourseFlow = () => {
 // State variables
 const [isMenuOpen, setIsMenuOpen] = useState(true);
 const [selectedContent, setSelectedContent] = useState(null);

 // Content click handler
 const handleContentClick = (index) => {
  setSelectedContent(index);
 };

 // Button click handler
 const handleButtonClick = () => {
  setIsMenuOpen(!isMenuOpen);
 };

 const courseData = [
  { title: 'Lecture 1', content: 'Content for Lecture 1...' },
  { title: 'Lecture 2', content: 'Content for Lecture 2...' },
  // ... more lectures
 ];

 return (
  <Container fluid className='course-container'>
   <Row>
   <Col md={9} className="d-flex flex-column">
    {/* Content box - now on the left */}
    <Col md={8} className="col-md-12">
     {/* Content goes here, always rendered */}
     <h2>{selectedContent !== null ? courseData[selectedContent].title : ''}</h2>
     <p>{selectedContent !== null ? courseData[selectedContent].content : ''}</p>
    </Col>
    </Col>


    {/* Menu column - now on the right */}
    <Col md={3} className="d-flex flex-column ms-auto"></Col>
    <Col md={4} className={`${isMenuOpen ? 'show' : ''} col-md-3 order-md-last`}>
     <Button variant="dark" onClick={handleButtonClick}>
      {isMenuOpen ? 'Hide Menu' : 'Show Menu'}
     </Button>
     <Collapse in={isMenuOpen}>
      <ListGroup>
       {courseData.map((lecture, index) => (
        <ListGroupItem
         key={index}
         action
         onClick={() => handleContentClick(index)}
        >
         {lecture.title}
        </ListGroupItem>
       ))}
      </ListGroup>
     </Collapse>
    </Col>
   </Row>
  </Container>
 );
};

export default CourseFlow;
