import React, { useState } from 'react';
import { ListGroup, ListGroupItem, Collapse, Container, Row, Col } from 'react-bootstrap';


const CourseFlowContent = (props) => {

 const [openContent, setOpenContent] = useState(null);

 const courseData = [
  { title: 'Lecture 1', content: 'Content for Lecture 1...' },
  { title: 'Lecture 2', content: 'Content for Lecture 2...' },
  // ... more lectures
 ];


 return (
  <Container fluid>
   <Row>
    <Col md={4}>
     {/* Collapsible list */}
     <ListGroup>
      {courseData.map((lecture, index) => (
       <ListGroupItem key={index} action onClick={() => setOpenContent(index)}>
        {lecture.title}
       </ListGroupItem>
      ))}
     </ListGroup>
    </Col>
    <Col md={8}>
     {/* Content box */}
     {openContent !== null && (
      <div>
       <h2>{courseData[openContent].title}</h2>
       <p>{courseData[openContent].content}</p>
      </div>
     )}
    </Col>
   </Row>
  </Container>

 );
}

export default CourseFlowContent;
