import CourseFlow from "./CourseFlow";
import ScrollToTop from "../../components/Common/ScrollTop";
import React from "react";

const CourseMain = () => {

	const courseData = {
		duration: 10,
		description: "<p>Este curso ofrece una visión buscan actualizar sus habilidades.</p>",
		detail: "<p>Bienvenido al curso de <strong>Iniciación al Marketing Digital</strong>, un potencial del marketing digital y dar un impulso significativo a tu carrera o negocio.</p>",
		category: [
			"Finances",
			"Trading"
		],
		modules: [
			{
				name: "Cryptomonedas",
				content: [
					{
						type: "video",
						title: "Kripto music",
						url: "https://www.youtube.com/watch?v=JygUVgWHGwU"
					},
					{
						text: "Las criptomonedas son activos digitales que usan criptografía para asegurar las transacciones y controlar la creación de nuevas unidades. Nacen como alternativa descentralizada a las monedas tradicionales. Bitcoin, la primera criptomoneda, fue lanzada en 2009. Su valor es volátil y se pueden usar para comprar bienes, servicios o invertir.",
						type: "text",
						title: "Las criptomonedas"
					},
					{
						questions: [
							{
								question: "¿Qué es una criptomoneda?",
								answers: [
									"Una moneda digital descentralizada.",
									"Un tipo de moneda virtual.",
									"Una forma de inversión en línea.",
									"Un sistema de pago electrónico"
								],
								correctAnswer: "Una moneda digital descentralizada."
							},
							{
								question: "¿Cuál es el nombre del primer exchange de criptomonedas descentralizado?",
								answers: [
									"Binance",
									"Coinbase",
									"Uniswap",
									"Kraken"
								],
								correctAnswer: "Uniswap"
							}
						],
						type: "quiz"
					}
				]
			},
			{
				name: "Criptografía",
				content: [
					{
						type: "video",
						title: "Video test 1",
						url: "https://www.youtube.com/watch?v=NUZZAN3jKTM&t"
					},
					{
						text: "Se ve toda la data del curso",
						type: "text",
						title: "Test article 1"
					},
					{
						questions: [
							{
								question: "Question 1",
								answers: [
									"11",
									"22",
									"33",
									"44"
								],
								correctAnswer: "22"
							}
						],
						type: "quiz"

					},

				]
			}
		]
	}



	return (
		<React.Fragment>

			{/* CoursePart Start */}
			{/* Contact Section Start */}
			<div className="rs-about style1 pt-50 pb-50 md-pt-50 md-pb-60  h-700" >
				<div className="container">
					<CourseFlow courseData={courseData} />
				</div>
			</div>
			{/* Contact Section End */}
			{/* CoursePart End */}

			{/* scrolltop-start */}
			<ScrollToTop scrollClassName="scrollup orange-color" />
			{/* scrolltop-end */}
		</React.Fragment>
	);
};


export default CourseMain;
