import React, {  useState } from "react";

const AddCourseForm = () => {

	// const [modules, setModules] = useState([
	// 	{
	// 		name: "",
	// 		content: [
	// 			{ type: "video", name: "", url: "" },
	// 			{ type: "text", title: "", text: "" },
	// 			{
	// 				type: "quiz", question: [{
	// 					question: "",
	// 					answers: ["", "", "", ""],
	// 					correctAnswer: "",
	// 				}]
	// 			}
	// 		],
	// 	},
	// ]);

	/* 

	handleInputChange
	handleChangeSelect
	handleModuleChange
	handleContentChange

	*/
	handleAddContent
handleDeleteContent



	const [modules, setModules] = useState([]);
	const [formData, setFormData] = useState({ courseName: "", description: "", modules: {} });

	const [selectedOption, setSelectedOption] = useState("video");

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};

	const handleChangeSelect = (e) => {
		setSelectedOption(e.target.value);
	};

	const handleModuleChange = (index, e) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];

		if (selectedOption !== "quiz") {
			updatedModules[index] = { ...updatedModules[index], [name]: value };
		} else {
			// Handle quiz-specific module data if needed
		}

		setModules(updatedModules);
	};


	const handleContentChange = (e, moduleIndex, contentIndex) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];
		const updatedContents = [...updatedModules[moduleIndex].content];
		if (!updatedContents[contentIndex]) {
			updatedContents[contentIndex] = {};
		}

		updatedContents[contentIndex] = {
			...updatedContents[contentIndex],
			[name]: value,
		};

		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};
		setModules(updatedModules);
	};

	const handleAddContent = (moduleIndex) => {
		const updatedModules = [...modules];
		const updatedContents = updatedModules[moduleIndex].content || [];
		updatedContents.push({ name: "", description: "" });
		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};
		setModules(updatedModules);
	};

	const handleDeleteContent = (moduleIndex, contentIndex) => {
		const updatedModules = [...modules];
		updatedModules[moduleIndex].content.splice(contentIndex, 1);
		setModules(updatedModules);
	};

	const handleDeleteModule = (moduleIndex) => {
		const updatedModules = [...modules];
		updatedModules.splice(moduleIndex, 1);
		setModules(updatedModules);
	};

	const handleAddModule = () => {
		setModules([...modules, { name: "", description: "", content: [] }]);
	};

	const [quizContent, setQuizContent] = useState([
		{
			question: "",
			answers: ["", "", "", ""],
			correct: "",
		},
	]);

	const handleQuestion = (e, index) => {
		const { name, value } = e.target;
		const updatedQuestion = [...quizContent];
		updatedQuestion[index] = { ...updatedQuestion[index], [name]: value };
		setQuizContent(updatedQuestion);
	};

	const handleAnswerChange = (questionIndex, answerIndex, e) => {
		const { value } = e.target;
		const updatedQuizContent = [...quizContent];

		if (!updatedQuizContent[questionIndex].answers) {
			updatedQuizContent[questionIndex].answers = [];
		}

		const updatedAnswers = [...updatedQuizContent[questionIndex].answers];
		updatedAnswers[answerIndex] = value;

		updatedQuizContent[questionIndex] = {
			...updatedQuizContent[questionIndex],
			answers: updatedAnswers,
		};

		setQuizContent(updatedQuizContent);
	};

	const handleCorrectAnswerChange = (event, questionIndex) => {
		const correctAnswerIndex = Number(event.target.value);
		const updatedQuizContent = [...quizContent];
		const updatedAnswers = { ...updatedQuizContent[questionIndex] };

		updatedAnswers.correct = updatedAnswers.answers[correctAnswerIndex];
		updatedQuizContent[questionIndex] = updatedAnswers;

		setQuizContent(updatedQuizContent);
	};

	const handleAddQuestion = () => {
		setQuizContent((prevQuizContent) => [
			...prevQuizContent,
			{
				question: "",
				answers: ["", "", "", ""],
				correct: "",
			},
		]);
	};

	const handleDeleteQuestion = (questionIndex) => {
		setQuizContent((prevQuizContent) => [
			...prevQuizContent.slice(0, questionIndex),
			...prevQuizContent.slice(questionIndex + 1),
		]);
	};



	const handleSubmit = (e) => {
		e.preventDefault();

	};



	////////////////////////////
	// SHOW DATA
	////////////////////////////
	const [showData, setShowData] = useState(true);
	const formatCourseStructure = () => {
		const object1 = {
			name: formData.courseName,
			description: formData.description,
			modules: modules.map((module) => ({
				name: module.name,
				description: module.description,
				content: module.content.map((content) => ({
					name: content.name,
					description: content.description,
				})),
			})),
		};

		return JSON.stringify(object1, null, 2);
	};

	return (
		<form onSubmit={handleSubmit} className="container mt-0 module-box box pb-40">
			<h2>Crear Curso:</h2>

			<div className="mb-3">
				<label className="form-label text-large">Nombre:</label>
				<input
					type="text"
					name="courseName"
					value={formData.courseName}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Nombre del curso"
				/>
			</div>

			<div className="mb-3">
				<label className="form-label mt-2">Descripcion:</label>
				<textarea
					name="description"
					value={formData.description}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Describa su curso aqui"
				/>
			</div>

			{/* Create Module Section Start */}
			{modules.length !== 0 && <h3 className="mt-50">Crear modulos:</h3>}

			{modules.map((module, moduleIndex) => (
				<div key={moduleIndex} className="mb-20 boxs px-3 py-4 rounded module-box">
					<div className="container">
						<div className="row">
							<div className="col-md-10">
								<div>
									<label className="form-label mt-2 mb-1">Titulo:</label>
									<input
										type="text"
										name="name"
										value={module.name}
										onChange={(e) => handleModuleChange(moduleIndex, e)}
										className="form-control mb-20"
										placeholder="Titulo del modulo"
									/>

								</div>
							</div>

							<div className="col-md-2 d-flex align-items-center justify-content-center">
								<div className="d-flex justify-content-center sl-btn">
									<button
										type="button"
										onClick={() => {
											handleDeleteModule(moduleIndex);
										}}
										className="btn btn-del-modul"
									>
										<i className="fa fa-trash"></i>
									</button>
								</div>
							</div>
						</div>
					</div>

					{module.content.length !== 0 && (
						<h3 className="mt-50">Crear contenido:</h3>
					)}

					{module.content.map((content, contentIndex) => (
						<div key={contentIndex} className="mb-20 px-3 py-4 rounded module-shadow">
							<div className="container">
								<div className="row">
									{/* Selector Option Start */}
									<div className="col-md-10">
										<div className="form-group">
											<h5 className="">Seleccione una opcion de creacion de contenido:</h5>
											<select
												className="form-control"
												value={selectedOption}
												onChange={(e) => {
													handleChangeSelect(e, moduleIndex);
												}}
											>
												<option value="video">Cargar video</option>
												<option value="text">Crear articulo</option>
												<option value="quiz">Crear quiz de preguntas y respuestas</option>
											</select>
										</div>
										{/* Selector Option End */}

										{/* Video Option Start */}
										{selectedOption === "video" && (
											<div>
												<h5 className="mt-50">Cargar video</h5>
												<label className="form-label mt-2 mb-1">Titulo del video:</label>
												<input
													type="text"
													name="name"
													value={content.name || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del contenido"
												/>

												<label className="form-label mt-2 mb-1">Url:</label>
												<input
													type="text"
													name="url"
													value={content.url || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="url del video"
												/>
											</div>
										)}
										{/* Video Option End */}

										{/* Text Option Start */}
										{selectedOption === "text" && (
											<div>
												<h5 className="mt-50">Crear articulo</h5>
												<label className="form-label mt-2 mb-1">Titulo:</label>
												<input
													type="text"
													name="title"
													value={content.title || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del articulo"
												/>

												<label className="form-label mt-2 mb-1">Articulo:</label>
												<textarea
													type="text"
													name="text"
													value={content.text || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Contenido del articulo"
												/>
											</div>
										)}
										{/* Text Option End */}

										{/* Quiz Option Start */}
										{selectedOption === "quiz" && (
											<div>
												{/* <Quiz
													handleQuestion={handleQuestion}
													handleAnswerChange={handleAnswerChange}
													handleCorrectAnswerChange={handleCorrectAnswerChange}
													handleAddQuestion={handleAddQuestion(moduleIndex)}
													handleDeleteQuestion={handleDeleteQuestion}
													quizContent={content}
												/> */}

												<div className="container mt-4 quiz style1">
													<h5 className="mt-50">Crear Quiz:</h5>

													{content.question.map((question, questionIndex) => (
														<div key={questionIndex} className="mb-20 rounded module-shadow">
															<div className="container">
																<div className="row contact-widget module-box">
																	<div className="col-md-10  rounded pt-3">
																		<div className="form-group">
																			<label>Ingrese la pregunta o trivia:</label>
																			<textarea
																				name="question"
																				className="form-control"
																				value={question.question}
																				onChange={(e) => handleQuestion(e, questionIndex)}
																				placeholder={`Pregunta...`}
																			></textarea>
																		</div>

																		<div className="form-group">
																			<label>Escriba las posibles respuestas:</label>
																			{question.answers.map((answer, answerIndex) => (
																				<div key={answerIndex} className="mb-10">
																					<input
																						name="answer"
																						type="text"
																						className="form-control"
																						value={answer}
																						onChange={(e) =>
																							handleAnswerChange(questionIndex, answerIndex, e)
																						}
																					/>
																				</div>
																			))}

																			<div className="form-group">
																				<label>Seleccione la respuesta correcta:</label>
																				<select
																					className="form-control"
																					value={question.answers.indexOf(question.correct)}
																					onChange={(e) =>
																						handleCorrectAnswerChange(e, questionIndex)
																					}
																				>
																					{question.answers.map((answer, index) => (
																						<option key={index} value={index}>
																							{answer}
																						</option>
																					))}
																				</select>
																			</div>
																		</div>
																	</div>

																	<div className="col-md-2 d-flex align-items-center justify-content-center">
																		<div className="d-flex justify-content-center">
																			<button
																				type="button"
																				onClick={() => {
																					handleDeleteQuestion(questionIndex);
																				}}
																				className="btn btn-del-cont "
																			>
																				<i className="fa fa-trash"></i>
																			</button>
																		</div>
																	</div>
																</div>
															</div>
														</div>
													))}

													<div className="container d-flex justify-content-center contact-widget">
														<button
															type="button"
															onClick={() => {
																handleAddQuestion(moduleIndex);
															}}
															className="btn  btn-add btn-add-cont me-2 m-10"
														>
															Agregar Pregunta
														</button>
													</div>

												</div>
											</div>
										)}
										{/* Quiz Option End */}
									</div>

									<div className="col-md-2 d-flex align-items-center justify-content-center">
										<div className="d-flex justify-content-center">
											<button
												type="button"
												onClick={() => {
													handleDeleteContent(moduleIndex);
												}}
												className="btn btn-del-cont "
											>
												<i className="fa fa-trash"></i>
											</button>
										</div>
									</div>
								</div>
							</div>
						</div>
					))}

					<div className="container d-flex justify-content-center">
						<button
							type="button"
							onClick={() => {
								handleAddContent(moduleIndex);
							}}
							className="btn btn-secondary btn-add btn-add-cont me-2 m-10"
						>
							Agregar Contenido
						</button>
					</div>
				</div>
			))}
			{/* Create Module Section End */}

			<div className="container d-flex justify-content-center">
				<button
					type="button"
					onClick={handleAddModule}
					className="btn btn-secondary btn-add break-words me-2"
				>
					Agregar Modulo
				</button>
			</div>

			<button type="submit" className="btn btn-send w-50 pt-50">
				Enviar formulario
			</button>

			<div className="data-box position-fixed top-0 end-0">
				<button onClick={() => setShowData(!showData)}>Toggle Data</button>
				{showData && (
					<div className="data">
						<pre>{formatCourseStructure()}</pre>
					</div>
				)}
			</div>
		</form>

	);
};

export default AddCourseForm;




