import React, { useState } from 'react';
import {
 ListGroup,
 ListGroupItem,
 Collapse,
 Container,
 Row,
 Col,
 Button,
} from 'react-bootstrap';

const CourseFlow = () => {

 // const [openContent, setOpenContent] = useState(null);
 const [isMenuOpen, setIsMenuOpen] = useState(true);
 const [selectedContent, setSelectedContent] = useState(null);

 const [isMainContentExpanded, setIsMainContentExpanded] = useState(true);


 const handleContentClick = (index) => {
  setSelectedContent(index);
 };

 const courseData = [
  { title: 'Lecture 1', content: 'Content for Lecture 1...' },
  { title: 'Lecture 2', content: 'Content for Lecture 2...' },
  // ... more lectures
 ];

 const handleButtonClick = () => {
  setIsMenuOpen(!isMenuOpen);
  setIsMainContentExpanded(!isMainContentExpanded);
};


 return (
  <Container fluid>
   <Row>
   <Col md={8} className={`${isMenuOpen ? 'col-md-9' : 'col-md-12'}`}>
     {/* Content box implementation - update based on your approach */}
     <Col md={8}>
      {selectedContent !== null && (
       <div>
        <h2>{courseData[selectedContent].title}</h2>
        <p>{courseData[selectedContent].content}</p>
       </div>
      )}
     </Col>
    </Col>



    <Col md={4} className={`${isMenuOpen ? 'show' : ''} col-md-3`}>
     <Button variant="dark" onClick={handleButtonClick}>
      {isMenuOpen ? 'Hide Menu' : 'Show Menu'}
     </Button>
     <Collapse in={isMenuOpen}>
      <ListGroup>
       {courseData.map((lecture, index) => (
        <ListGroupItem key={index} action onClick={() => handleContentClick(index)}>
         {lecture.title}
        </ListGroupItem>
       ))}
      </ListGroup>
     </Collapse>
    </Col>
   </Row>
  </Container>

 );
}

export default CourseFlow;
