import React, { useEffect, useState } from "react";
import Quiz from "./Quiz";

const AddCourseForm = () => {
	const [modules, setModules] = useState([]);
	const [formData, setFormData] = useState({
		courseName: "",
		description: "",
		modules: {},
	});
	const [selectedOption, setSelectedOption] = useState("video");
