		{
			question: "",
			answers: ["", "", "", ""],
			correctAnswer: "",
		},
	]);

	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};

	const handleChangeSelect = (e, moduleIndex) => {
		setSelectedOption(e.target.value);

		const updatedModules = [...modules];
		const updatedContents = [];

		if (e.target.value === "video") {
			updatedContents.push({ type: "video", url: "" });
		} else if (e.target.value === "text") {
			updatedContents.push({ type: "texto", texto: "" });
		} else if (e.target.value === "quiz") {
			const quizData = {
				question: "",
				answers: ["", "", "", ""],
				correctAnswer: "",
			};

			updatedContents.push({ type: "quiz", question: [quizData] });
		}

		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};

		setModules(updatedModules);
	};

	const handleModuleChange = (index, e) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];

		if (selectedOption !== "quiz") {
			updatedModules[index] = { ...updatedModules[index], [name]: value };
		} else {
			const quizData = {
				question: "",
				answers: ["", "", "", ""],
				correctAnswer: "",
			};

			updatedModules[index] = {
				...updatedModules[index],
				[name]: value,
				content: [{ quizData }],
			};
		}

		setModules(updatedModules);
	};

	const handleContentChange = (e, moduleIndex, contentIndex) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];
		const updatedContents = [...updatedModules[moduleIndex].content];

		if (!updatedContents[contentIndex]) {
			updatedContents[contentIndex] = {};
		}

		updatedContents[contentIndex] = {
			...updatedContents[contentIndex],
			[name]: value,
		};

		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};
		setModules(updatedModules);
	};

	const handleAddContent = (moduleIndex) => {
		const updatedModules = [...modules];
		const updatedContents = updatedModules[moduleIndex].content || [];

		if (selectedOption === "video") {
			updatedContents.push({ type: selectedOption, url: "" });
		} else if (selectedOption === "text") {
			updatedContents.push({ type: selectedOption, texto: "" });
