import React, { useState } from 'react';
import {
 ListGroup,
 ListGroupItem,
 Collapse,
 Container,
 Row,
 Col,
 Button,
} from 'react-bootstrap';

const CourseFlow = () => {

 const [openContent, setOpenContent] = useState(null);
 const [isMenuOpen, setIsMenuOpen] = useState(true);
 const [selectedContent, setSelectedContent] = useState(null);

 const handleContentClick = (index) => {
  setSelectedContent(index);
};

 const courseData = [
  { title: 'Lecture 1', content: 'Content for Lecture 1...' },
  { title: 'Lecture 2', content: 'Content for Lecture 2...' },
  // ... more lectures
 ];


 return (
  <Container fluid>
   <Row>
    <Col md={4}>
     <Button variant="dark" onClick={() => setIsMenuOpen(!isMenuOpen)}>
      {isMenuOpen ? 'Hide Menu' : 'Show Menu'}
     </Button>
     <Collapse in={isMenuOpen}>
      <ListGroup>
       {courseData.map((lecture, index) => (
        <ListGroupItem key={index} action onClick={() => handleContentClick(index)}>
         {lecture.title}
        </ListGroupItem>
       ))}
      </ListGroup>
     </Collapse>
    </Col>
    <Col md={8}>
     {/* Content box implementation - update based on your approach */}
    </Col>
   </Row>
  </Container>

 );
}

export default CourseFlow;
