import React, { useState } from 'react';
import { ListGroup, ListGroupItem, Collapse, Container, Row, Col } from 'react-bootstrap';

const CourseFlow = ({ courseData }) => {
  // State variables
  const [openModuleIndexes, setOpenModuleIndexes] = useState([]);

  // Content click handler
  const handleContentClick = (index) => {
    // Handle content click here
  };

  // Module click handler
  const handleModuleClick = (index) => {
    if (openModuleIndexes.includes(index)) {
      setOpenModuleIndexes(openModuleIndexes.filter((i) => i !== index));
    } else {
      setOpenModuleIndexes([...openModuleIndexes, index]);
    }
  };

  return (
    <Container fluid className="course-container outred position-relative">
      <Row>
        {/* Content column with dynamic width */}
        <Col xs={12} md={9} className="d-flex flex-column">
          {/* Content box */}
          <div className="content-box">
            {/* Render content based on selectedContent */}
            {/* Placeholder for rendering content */}
          </div>
        </Col>

        {/* Menu column */}
        <Col xs={12} md={3} className="d-flex flex-column">
          {/* Render menu items based on modules */}
          {courseData.modules.map((module, index) => (
            <div key={index} className="mb-3">
              <ListGroupItem
                onClick={() => handleModuleClick(index)}
                style={{ cursor: 'pointer' }}
              >
                {module.name}
              </ListGroupItem>
              <Collapse in={openModuleIndexes.includes(index)}>
                <ListGroup className='content-menu'>
                  {module.content.map((content, contentIndex) => (
                    <ListGroupItem
                      key={`${index}-${contentIndex}`}
                      action
                      onClick={() => handleContentClick(contentIndex)}
                    >
                      {content.type === 'quiz' ? 'Quiz' : content.title}
                    </ListGroupItem>
                  ))}
                </ListGroup>
              </Collapse>
            </div>
          ))}
        </Col>
      </Row>
    </Container>
  );
};

export default CourseFlow;
