import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import CourseSingleTwo from "../../components/Courses/CourseSingleTwo";
import ReactPaginate from "react-paginate";

// Course courseImg
import courseImg1 from "../../assets/img/courses/1.jpg";

const AdminAllCourses = () => {
  const [fetchData, setFetchData] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const coursesPerPage = 9;

  useEffect(() => {
    const getCourses = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_GET_ALL}`
        );
        const data = await response.json();
        setFetchData(data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    getCourses();
  }, []);

  const handlePageChange = ({ selected }) => {
    window.scrollTo({
      top: 250,
      behavior: "smooth",
    });
    setCurrentPage(selected);
  };

  const totalCourses = fetchData.length;
  const pageCount = Math.ceil(totalCourses / coursesPerPage);

  const renderCourses = () => {
    const startIndex = currentPage * coursesPerPage;
    const selectedCourses = fetchData.slice(startIndex, startIndex + coursesPerPage);

    return selectedCourses.map(course => (
      <div key={course.id}>
        <p>{course.courseData.name}</p>
        <p>Active: {course.active ? "Yes" : "No"}</p>
        <button onClick={() => handleActivate(course.id)}>Activate</button>
        <button onClick={() => handleDelete(course.id)}>Delete</button>
        <button onClick={() => handleModify(course.id)}>Modify</button>
      </div>
    ));
  };

  const handleActivate = (id) => {
    // Logic to activate the course with the given id
  };

  const handleDelete = (id) => {
    // Logic to delete the course with the given id
  };

  const handleModify = (id) => {
    // Logic to modify the course with the given id
  };

  return (
    <div>
      {renderCourses()}
      <ReactPaginate
        previousLabel={"Previous"}
        nextLabel={"Next"}
        breakLabel={"..."}
        pageCount={pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageChange}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </div>
  );
};
export default AdminAllCourses;
