import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const QuizContent = ({ quizData }) => {
  const [selectedAnswers, setSelectedAnswers] = useState(new Array(quizData.questions.length).fill(null));
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleOptionSelect = (questionIndex, answerIndex) => {
    if (!isSubmitted) {
      const newAnswers = [...selectedAnswers];
      newAnswers[questionIndex] = answerIndex === newAnswers[questionIndex] ? null : answerIndex;
      setSelectedAnswers(newAnswers);
    }
  };

  const handleSubmit = () => {
    setIsSubmitted(true);
    const totalQuestions = quizData.questions.length;
    const totalCorrectAnswers = selectedAnswers.reduce((acc, selectedAnswer, questionIndex) => {
      const correctAnswerIndex = parseInt(quizData.questions[questionIndex].correctAnswer);
      console.log('Selected Answer:', selectedAnswer);
      console.log('Correct Answer Index:', correctAnswerIndex);
      console.log('Question Index:', questionIndex);
      console.log('Question:', quizData.questions[questionIndex]);
      console.log('-------------');
      return selectedAnswer === correctAnswerIndex ? acc + 1 : acc;
    }, 0);

    console.log({ totalQuestions, totalCorrectAnswers });
  };

  return (
    <div className="content-box quiz-content">
      {/* Render quiz questions and options */}
      {quizData.questions.map((question, questionIndex) => (
        <div key={questionIndex} className="question-container">
          <h3>{question.question}</h3>
          <div className="options-container">
            {question.answers.map((answer, answerIndex) => {
              const isCorrect = isSubmitted && selectedAnswers[questionIndex] === answerIndex && question.correctAnswer === answerIndex;
              const isSelected = selectedAnswers[questionIndex] === answerIndex;
              return (
                <div
                  key={answerIndex}
                  className={`option ${isSelected ? 'selected' : ''} ${isCorrect ? 'correct' : ''} ${isSubmitted && isSelected && !isCorrect ? 'incorrect' : ''}`}
                  onClick={() => handleOptionSelect(questionIndex, answerIndex)}
                  style={{ backgroundColor: isSelected ? '#f0f0f0' : 'initial' }}
                >
                  {answer}
                </div>
              );
            })}
          </div>
        </div>
      ))}
      {/* Submit button */}
      <Button onClick={handleSubmit} disabled={isSubmitted}>Submit</Button>
    </div>
  );
};

export default QuizContent;
