 ////////////////////////////
 // GET CATEGORIES DATA
 ////////////////////////////
 const [categories, setCategories] = useState([]);

 useEffect(() => {
  const fetchData = async () => {
   try {
    const response = await fetch(
     `${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_CATEGORIES_GET_ALL}`
    );
    const data = await response.json();
    const filtered = data.filter((category) => category.active === true);
    setCategories(filtered);
    // setActiveCategory(filtered[0].name);
   } catch (error) {
    console.error("Error fetching categories:", error);
   }
  };

  fetchData();
  // eslint-disable-next-line react-hooks/exhaustive-deps
 }, []);

 ////////////////////////////
 // RENDER CATEGORIES TABS
 ////////////////////////////
 const [activeCategory, setActiveCategory] = useState(undefined);
 const [currentPage, setCurrentPage] = useState(0);
 const coursesPerPage = 9;

 let coursesCount = 0; // need this one outside in order to be 

 const renderCoursePanel = () => {
  let filteredCourses;
  if (activeCategory === undefined) {
   filteredCourses = fetchData;
   coursesCount = filteredCourses.length;
  } else {
   // Filter courses based on active category
   filteredCourses = fetchData.filter((course) =>
    course.category.includes(activeCategory)
   );
   coursesCount = filteredCourses.length;

   console.log("filtered Courses ", filteredCourses);
  }

  const offset = currentPage * coursesPerPage;
  const currentPageCourses = filteredCourses.slice(
   offset,
   offset + coursesPerPage
  );

  console.log("current ", currentPageCourses);

  return (
   <div className="row">
    {currentPageCourses.map((course) => (
     <div key={course.id} className="col-lg-4 col-md-6">
      <CourseSingleTwo
       courseClass="courses-item md-mb-30"
       courseImg={courseImg1}
       courseTitle={course.name || "Curso en desarrollo"}
       courseDuration={course.duration || "10"}
       courseId={course.id || "12312312"}
       courseCategory={course.category || "En desarrollo"}
      />
     </div>
    ))}
   </div>
  );
 };



 const handlePageChange = ({ selected }) => {
  window.scrollTo({
   top: 250,
   behavior: "smooth",
  });
  setCurrentPage(selected);
 };

 return (
  <div
   id="rs-popular-course"
   className="rs-popular-courses style1 orange-style pt-50 pb-100 md-pt-70 md-pb-50"
  >
   <div className="container">
    <Tabs>
     <TabList className="gridFilter text-center mb-10 md-mb-10">
      <Tab key="Miscellaneous" onClick={() => setActiveCategory(undefined)}>
       <button>Miscellaneous</button>
      </Tab>
      {categories.map((category) => (
       <Tab
        key={category.id}
        onClick={() => {
         setActiveCategory(category.name);
         console.log("category ", category.name);
        }}
       >
        <button>{category.name}</button>
       </Tab>
      ))}
     </TabList>

     <TabPanel>
      <div>
       <h2>{activeCategory || "All Categories"}</h2>
       {renderCoursePanel()}
       <ReactPaginate
        className="pagination"
        previousLabel={"Previous"}
        nextLabel={"Next"}
        breakLabel={"..."}
        pageCount={Math.ceil(coursesCount.length / coursesPerPage)}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageChange}
        containerClassName={"pagination"}
        activeClassName={"active"}
       />
      </div>
     </TabPanel>
    </Tabs>
   </div>
  </div>
 );
