import React, { useEffect, useState } from "react";
import Quiz from "./Quiz";

const AddCourseForm = () => {
	const [modules, setModules] = useState([]);
	const [formData, setFormData] = useState({
		courseName: "",
		description: "",
		modules: {},
	});
	const [selectedOption, setSelectedOption] = useState("video");
	const [quizContent, setQuizContent] = useState([
		{
			question: "",
			answers: ["", "", "", ""],
			correctAnswer: "",
		},
	]);

	///////////////////////////////////
	// FUNCTIONS WORKING
	///////////////////////////////////
	const handleChangeSelect = (e, moduleIndex) => {
		setSelectedOption(e.target.value);

		setModules(prevModules => {
						return prevModules.map((module, idx) => {
										if (idx === moduleIndex) {
														const updatedContents = [];

														if (e.target.value === "video") {
																		updatedContents.push({ type: "video", url: "" });
														} else if (e.target.value === "text") {
																		updatedContents.push({ type: "text", text: "" });
														} else if (e.target.value === "quiz") {
																		const quizData = {
																						question: "",
																						answers: ["", "", "", ""],
																						correctAnswer: "",
																		};
																		updatedContents.push({ type: "quiz", questions: [quizData] });
														}

														return {
																		...module,
																		content: updatedContents,
														};
										}
										return module;
						});
		});
};



	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};


	const handleDeleteContent = (moduleIndex, contentIndex) => {
		const updatedModules = [...modules];
		updatedModules[moduleIndex].content.splice(contentIndex, 1);
		setModules(updatedModules);
	};

	const handleDeleteModule = (moduleIndex) => {
		const updatedModules = [...modules];
		updatedModules.splice(moduleIndex, 1);
		setModules(updatedModules);
	};

	const handleAddModule = () => {
		setModules([...modules, { name: "", content: [] }]);
	};

	const handleModuleChange = (index, e) => {
		const { name, value } = e.target;

		setModules(prevModules => {
			return prevModules.map((module, idx) => {
				if (idx === index) {
					return {
						...module,
						[name]: value
					};
				}
				return module;
			});
		});
	};
	const handleContentChange = (e, moduleIndex, contentIndex) => {
		const { name, value } = e.target;

		setModules(prevModules => {
						return prevModules.map((module, idx) => {
										if (idx === moduleIndex) {
														const updatedContents = module.content.map((content, cIdx) => {
																		if (cIdx === contentIndex) {
																						if (module.content[contentIndex].type === "video" && name === "name") {
																										return { ...content, title: value };
																						} else if (module.content[contentIndex].type === "video" && name === "url") {
																										return { ...content, url: value };
																						} else if (module.content[contentIndex].type === "text" && name === "title") {
																										return { ...content, title: value };
																						} else if (module.content[contentIndex].type === "text" && name === "text") {
																										return { ...content, text: value };
																						} else {
																										return content;
																						}
																		}
																		return content;
														});

														return {
																		...module,
																		content: updatedContents,
														};
										}
										return module;
						});
		});
};


const handleAddContent = (moduleIndex) => {
    setModules(prevModules => {
        return prevModules.map((module, idx) => {
            if (idx === moduleIndex) {
                const updatedContents = [...module.content || []];

                if (selectedOption === "video") {
                    updatedContents.push({ type: "video", url: "" });
                } else if (selectedOption === "text") {
                    updatedContents.push({ type: "text", text: "" });
                } else if (selectedOption === "quiz") {
                    const quizData = {
                        question: "",
                        answers: ["", "", "", ""],
                        correctAnswer: "",
                    };
                    updatedContents.push({ type: "quiz", questions: [quizData] });
                }

                return {
                    ...module,
                    content: updatedContents,
                };
            }
            return module;
        });
    });
};





	/* Quiz Functions Section Start */
	const handleQuestion = (e, index) => {
		const { name, value } = e.target;

		setModules((prevModules) => {
			return prevModules.map((module) => {
				return {
					...module,
					content: module.content.map((item) => {
						if (item.type === "quiz" && Array.isArray(item.question)) {
							return {
								...item,
								question: item.question.map((question, qIndex) => {
									if (qIndex === index) {
										return {
											...question,
											[name]: value,
										};
									}
									return question;
								}),
							};
						}
						return item;
					}),
				};
			});
		});
	};

	const handleAnswerChange = (questionIndex, answerIndex, e) => {
		const { value } = e.target;

		setModules((prevModules) => {
			return prevModules.map((module) => {
				return {
					...module,
					content: module.content.map((item) => {
						if (item.type === "quiz" && Array.isArray(item.question)) {
							return {
								...item,
								question: item.question.map((question, qIndex) => {
									if (qIndex === questionIndex) {
										const updatedAnswers = [...question.answers];
										updatedAnswers[answerIndex] = value;

										return {
											...question,
											answers: updatedAnswers,
										};
									}
									return question;
								}),
							};
						}
						return item;
					}),
				};
			});
		});
	};

	const handleCorrectAnswerChange = (event, questionIndex) => {
		const correctAnswerIndex = Number(event.target.value);
		const updatedQuizContent = [...quizContent];
		const updatedAnswers = { ...updatedQuizContent[questionIndex] };

		updatedAnswers.correctAnswer = updatedAnswers.options[correctAnswerIndex];
		updatedQuizContent[questionIndex] = updatedAnswers;

		setQuizContent(updatedQuizContent);
	};

	const handleAddQuestion = (moduleIndex) => {
		const updatedModules = [...modules];

		if (moduleIndex >= 0 && moduleIndex < updatedModules.length) {
			const currentModule = updatedModules[moduleIndex];

			if (currentModule.content && Array.isArray(currentModule.content)) {
				const quizContentIndex = currentModule.content.findIndex(
					(item) => item.type === "quiz"
				);

				if (quizContentIndex !== -1) {
					let updatedQuestions;

					if (Array.isArray(currentModule.content[quizContentIndex].question)) {
						updatedQuestions = [...currentModule.content[quizContentIndex].question];
					} else {
						updatedQuestions = [];
					}

					updatedQuestions.push({
						question: "",
						answers: ["", "", "", ""],
						correctAnswer: "",
					});

					updatedModules[moduleIndex].content[quizContentIndex].question =
						updatedQuestions;

					setModules(updatedModules);
				}
			}
		}
	};

	const handleDeleteQuestion = (questionIndex) => {
		setQuizContent((prevQuizContent) => [
			...prevQuizContent.slice(0, questionIndex),
			...prevQuizContent.slice(questionIndex + 1),
		]);
	};
	/* Quiz Functions Section End */

	const handleSubmit = (e) => {
		e.preventDefault();

		const course = {
			name: formData.courseName,
			description: formData.description,
			modules: modules.map((module) => ({
				name: module.name,
				description: module.description,
				content: module.content.map((content) => ({
					name: content.name,
					description: content.description,
				})),
			})),
		};

		console.log("course data ", course);

		// setModules([
		// 	{ name: "", description: "", content: [{}] },
		// ]);
		// setFormData({ courseName: "", description: "" });
	};



	////////////////////////////
	// SHOW DATA
	////////////////////////////
	const [showData, setShowData] = useState(true);
	const formatCourseStructure = () => {
		const course = {
			name: formData.courseName,
			description: formData.description,
			modules: modules.map((module) => ({
				name: module.name,
				description: module.description,
				content: module.content.map((content) => ({
					name: content.name,
					description: content.description,
				})),
			})),
		};

		return JSON.stringify(course, null, 2);
	};

	return (
		<form onSubmit={handleSubmit} className="container mt-0 module-box box pb-40">
			<h2>Crear Curso:</h2>

			<div className="mb-3">
				<label className="form-label text-large">Nombre:</label>
				<input
					type="text"
					name="courseName"
					value={formData.courseName}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Nombre del curso"
				/>
			</div>

			<div className="mb-3">
				<label className="form-label mt-2">Descripcion:</label>
				<textarea
					name="description"
					value={formData.description}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Describa su curso aqui"
				/>
			</div>

			{/* Create Module Section Start */}
			{modules.length !== 0 && <h3 className="mt-50">Crear modulos:</h3>}

			{modules.map((module, moduleIndex) => (
				<div key={moduleIndex} className="mb-20 boxs px-3 py-4 rounded module-box">
					<div className="container">
						<div className="row">
							<div className="col-md-10">
								<div>
									<label className="form-label mt-2 mb-1">Titulo:</label>
									<input
										type="text"
										name="name"
										value={module.name}
										onChange={(e) => handleModuleChange(moduleIndex, e)}
										className="form-control mb-20"
										placeholder="Titulo del modulo"
									/>

								</div>
							</div>

							<div className="col-md-2 d-flex align-items-center justify-content-center">
								<div className="d-flex justify-content-center sl-btn">
									<button
										type="button"
										onClick={() => {
											handleDeleteModule(moduleIndex);
										}}
										className="btn btn-del-modul"
									>
										<i className="fa fa-trash"></i>
									</button>
								</div>
							</div>
						</div>
					</div>

					{module.content.length !== 0 && (
						<h3 className="mt-50">Crear contenido:</h3>
					)}

					{module.content.map((content, contentIndex) => (
						<div key={contentIndex} className="mb-20 px-3 py-4 rounded module-shadow">
							<div className="container">
								<div className="row">
									{/* Selector Option Start */}
									<div className="col-md-10">
										<div className="form-group">
											<h5 className="">Seleccione una opcion de creacion de contenido:</h5>
											<select
												className="form-control"
												value={selectedOption}
												onChange={(e) => {
													handleChangeSelect(e, moduleIndex);
												}}
											>
												<option value="video">Cargar video</option>
												<option value="text">Crear articulo</option>
												<option value="quiz">Crear quiz de preguntas y respuestas</option>
											</select>
										</div>
										{/* Selector Option End */}

										{/* Video Option Start */}
										{selectedOption === "video" && (
											<div>
												<h5 className="mt-50">Cargar video</h5>
												<label className="form-label mt-2 mb-1">Titulo del video:</label>
												<input
													type="text"
													name="name"
													value={content.name || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del contenido"
												/>

												<label className="form-label mt-2 mb-1">Url:</label>
												<input
													type="text"
													name="url"
													value={content.url || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
