	return (
		<div
			id="rs-popular-course"
			className="rs-popular-courses style1 orange-style pt-50 pb-100 md-pt-70 md-pb-50"
		>
			<div className="container">
				<Tabs>
					<TabList className={tabStyle}>
						{categories.map((category) => (
							<Tab key={category.id} onClick={() => setActiveCategory(category.name)}>
								<button>{category.name}</button>
							</Tab>
						))}
					</TabList>
					{categories.map((category) => (
						<TabPanel key={category.id}>
							<div className="row">
								{activeCategory === category.name && (
									<div>
										<h2>{activeCategory}</h2>
										{renderCoursePanel()}
										<ReactPaginate
											className="pagination"
											previousLabel={"Previous"}
											nextLabel={"Next"}
											breakLabel={"..."}
											pageCount={Math.ceil(filteredCourses.length / coursesPerPage)}
											marginPagesDisplayed={2}
											pageRangeDisplayed={5}
											onPageChange={handlePageChange}
											containerClassName={"pagination"}
											activeClassName={"active"}
										/>
									</div>
								)}
							</div>
						</TabPanel>
					))}
				</Tabs>
			</div>
		</div>
	);
};

export default CoursePart;
