import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";

const AdminAllCourses = () => {
  const [fetchData, setFetchData] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const coursesPerPage = 9;

  useEffect(() => {
    const getCourses = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_GET_ALL}`
        );
        const data = await response.json();
        setFetchData(data);
      } catch (error) {
        console.error("Error fetching courses:", error);
      }
    };

    getCourses();
  }, []);

  const handlePageChange = ({ selected }) => {
    window.scrollTo({
      top: 250,
      behavior: "smooth",
    });
    setCurrentPage(selected);
  };

  const totalCourses = fetchData.length;
  const pageCount = Math.ceil(totalCourses / coursesPerPage);

  const renderCourses = () => {
    const startIndex = currentPage * coursesPerPage;
    const selectedCourses = fetchData.slice(startIndex, startIndex + coursesPerPage);



    return selectedCourses.map(course => (
      <div key={course.id} className="course-item row align-items-center d-flex flex-wrap border rounded p-3 mb-3">
          <div className="col-lg-9 col-md-9 col-sm-12 col-xs-12 d-flex align-items-center justify-content-center">
              <p className="course-name">{course.courseData.name}</p>
          </div>
          <div className="col-lg-3 col-md-3 col-sm-12 col-xs-12 d-flex justify-content-end">
              <button 
                  className={`btn ${course.active ? "btn-secondary" : "btn-success"} me-2`} 
                  onClick={() => handleActivate(course.id)}
              >
                  {course.active ? "Deactivate" : "Activate"}
              </button>
              <button className="btn btn-danger me-2" onClick={() => handleDelete(course.id)}>
                  Delete
              </button>
              <button className="btn btn-primary" onClick={() => handleModify(course.id)}>
                  Modify
              </button>
          </div>
      </div>
    ));


  };

  const handleActivate = (id) => {
    // Logic to activate the course with the given id
  };

  const handleDelete = (id) => {
    // Logic to delete the course with the given id
  };

  const handleModify = (id) => {
    // Logic to modify the course with the given id
  };

  return (
    <div className="container mt-4">
      {renderCourses()}
      <ReactPaginate
        previousLabel={"Previous"}
        nextLabel={"Next"}
        breakLabel={"..."}
        pageCount={pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageChange}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </div>
  );
};
export default AdminAllCourses;
