import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import CourseSingleTwo from "../../components/Courses/CourseSingleTwo";
import ReactPaginate from "react-paginate";

// Course courseImg
import courseImg1 from "../../assets/img/courses/1.jpg";

const CoursePart = () => {
 const fetchData = [
  {
   id: 1755,
   name: "Investment Strategies",
   detail: "Explore various investment strategies and techniques.",
   duration: 12,
   category: ["Web development"],
  },
  {
   id: 5092,
   name: "Budgeting Essentials",
   detail: "Master the art of budgeting for personal and business finances.",
   duration: 6,
   category: ["Finances", "Crypto"],
  },
  {
   id: 6176,
   name: "Accounting Principles",
   detail: "Understand the fundamental principles of accounting.",
   duration: 10,
   category: ["Finances", "Trading", "Crypto"],
  },
  {
   id: 12346,
   name: "Cryptocurrency Trading",
   detail: "Learn about trading cryptocurrencies like Bitcoin and Ethereum.",
   duration: 9,
   category: ["Trading"],
  },
  {
   id: 12349,
   name: "Risk Management in Trading",
   detail: "Master the art of risk management in trading.",
   duration: 7,
   category: ["Trading"],
  },
 ];

 ////////////////////////////
 // RENDER CATEGORIES TABS
 ////////////////////////////
 const [fetchedCategories, setFetchedCategories] = useState([]);
  const [activeCategory, setActiveCategory] = useState(undefined);
  const [currentPage, setCurrentPage] = useState(0);
  const coursesPerPage = 9;

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await fetch(
          `${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_CATEGORIES_GET_ALL}`
        );
        const data = await response.json();
        const filtered = data.filter((category) => category.active === true);
        setFetchedCategories(filtered);
        setActiveCategory(filtered[0]?.name);
      } catch (error) {
        console.error("Error fetching categories:", error);
      }
    };

    fetchCategories();
  }, []);

  const renderCoursePanel = (selectedCategory) => {
    const filteredCourses = fetchData.filter((course) =>
      selectedCategory === "All courses"
        ? true
        : course.category.includes(selectedCategory)
    );

    const offset = currentPage * coursesPerPage;
    const currentPageCourses = filteredCourses.slice(
      offset,
      offset + coursesPerPage
    );

    return (
      <div className="row">
        {currentPageCourses.map((course) => (
          <div key={course.id} className="col-lg-4 col-md-6">
            <CourseSingleTwo
              courseClass="courses-item md-mb-30"
              courseImg={courseImg1}
              courseTitle={course.name || "Curso en desarrollo"}
              courseDuration={course.duration || "10"}
              courseId={course.id || "12312312"}
              courseCategory={
                selectedCategory === "All courses"
                  ? course.category.join(", ")
                  : selectedCategory
              }
            />
          </div>
        ))}
      </div>
    );
  };

  const handleTabClick = (category) => {
    setActiveCategory(category);
    setCurrentPage(0);
  };

  const handlePageChange = ({ selected }) => {
    window.scrollTo({
      top: 250,
      behavior: "smooth",
    });
    setCurrentPage(selected);
  };

  const totalCourses = activeCategory === "All courses"
    ? fetchData.length
    : fetchData.filter((course) =>
        course.category.includes(activeCategory)
      ).length;

  const pageCount = Math.ceil(totalCourses / coursesPerPage);

  return (
    <div
      id="rs-popular-course"
      className="rs-popular-courses style1 orange-style pt-50 pb-100 md-pt-70 md-pb-50"
    >
      <div className="container">
        <Tabs>
          <TabList className="gridFilter text-center mb-10 md-mb-10">
            <Tab key="All" onClick={() => handleTabClick("All courses")}>
              <button>All courses</button>
            </Tab>
            {fetchedCategories.map((category) => (
              <Tab key={category.id} onClick={() => handleTabClick(category.name)}>
                <button>{category.name}</button>
              </Tab>
            ))}
          </TabList>

          {fetchedCategories.map((category) => (
            <TabPanel key={category.id}>
              <div>
                <h2>{category.name}</h2>
                {renderCoursePanel(category.name)}
                <ReactPaginate
                  className="pagination"
                  previousLabel={"Previous"}
                  nextLabel={"Next"}
                  breakLabel={"..."}
                  pageCount={pageCount}
                  marginPagesDisplayed={2}
                  pageRangeDisplayed={5}
                  onPageChange={handlePageChange}
                  containerClassName={"pagination"}
                  activeClassName={"active"}
                />
              </div>
            </TabPanel>
          ))}
        </Tabs>
      </div>
    </div>
  );
};

export default CoursePart;
