import React, { useState } from 'react';
import { ListGroup, ListGroupItem, Collapse, Container, Row, Col, Button } from 'react-bootstrap';
import VideoContent from "../../components/CourseFlow/VideoContent";
import TextContent from "../../components/CourseFlow/TextContent";
import QuizContent from "../../components/CourseFlow/QuizContent";

const CourseFlow = ({ courseData }) => {
 // State variables
 const [openModuleIndexes, setOpenModuleIndexes] = useState([]);
 const [selectedContent, setSelectedContent] = useState(null);
 const [menuOpen, setMenuOpen] = useState(true);

 // Content click handler
 const handleContentClick = (moduleIndex, contentIndex) => {
  setSelectedContent({ moduleIndex, contentIndex });
 };

 // Module click handler
 const handleModuleClick = (index) => {
  if (openModuleIndexes.includes(index)) {
   setOpenModuleIndexes(openModuleIndexes.filter((i) => i !== index));
  } else {
   setOpenModuleIndexes([...openModuleIndexes, index]);
  }
 };

 // Toggle menu handler
 const toggleMenu = () => {
  setMenuOpen(!menuOpen);
 };

 return (
  <Container fluid className="course-container position-relative px-0 py-3">
   <Row className='m-0'>
    {/* Content column with dynamic width */}
    <Col xs={12} md={menuOpen ? 9 : 12} className="d-flex flex-column">
     {/* Content box */}
     <div className="content-box">
      {/* Render selected content */}
      {selectedContent !== null && (
       <>
        {courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex].type === 'video' && (
         <VideoContent content={courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex]} />
        )}
        {courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex].type === 'text' && (
         <TextContent content={courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex]} />
        )}
        {courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex].type === 'quiz' && (
         <QuizContent content={courseData.modules[selectedContent.moduleIndex].content[selectedContent.contentIndex]} />
        )}
       </>
      )}
     </div>
    </Col>

    {/* Menu column */}
    {menuOpen && (
     <Col xs={12} md={3} className="d-flex flex-column content-menu">
      {/* Render menu items based on modules */}
      {courseData.modules.map((module, moduleIndex) => (
       <div key={moduleIndex} className="mb-1">
        <ListGroupItem
         onClick={() => handleModuleClick(moduleIndex)}
         style={{ cursor: 'pointer' }}
         className="button-list"
        >
         {module.name}
        </ListGroupItem>
        <Collapse in={openModuleIndexes.includes(moduleIndex)}>
         <ListGroup>
          {module.content.map((content, contentIndex) => (
           <ListGroupItem
            key={`${moduleIndex}-${contentIndex}`}
            action
            onClick={() => handleContentClick(moduleIndex, contentIndex)}
            style={{ cursor: 'pointer' }}
            className='button-menu'
           >
            {content.type === 'quiz' ? 'Quiz' : content.title}
            {content.completed ? ( <i className="c fas fa-check"></i>) : content.title}

           </ListGroupItem>
          ))}
         </ListGroup>
        </Collapse>
       </div>
      ))}
     </Col>
    )}

    {/* Button to toggle menu */}
    <Button onClick={toggleMenu} className={`menu-toggle-button position-absolute top-1 ${menuOpen ? 'pill-position' : 'end-0'} pill-button`}>
     {menuOpen ? (<i class="fa fa-chevron-right"></i>) : (<i class="fa fa-chevron-left"></i>)}
    </Button>
   </Row>
  </Container>
 );
};

export default CourseFlow;
