import React, { useState } from "react";
import Quiz from "./Quiz";

const AddCourseForm = () => {
	const [modules, setModules] = useState([]);
	const [formData, setFormData] = useState({ courseName: "", description: "" });
	// const onAddCourse = useStore((state) => state.onAddCourse);

	const [selectedOption, setSelectedOption] = useState("video");
	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};

	const handleChangeSelect = (e) => {
		setSelectedOption(e.target.value);
	};

	const handleModuleChange = (index, e) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];
		updatedModules[index] = { ...updatedModules[index], [name]: value };
		setModules(updatedModules);
	};

	const handleContentChange = (e, moduleIndex, contentIndex) => {
		const { name, value } = e.target;
		const updatedModules = [...modules];
		const updatedContents = [...updatedModules[moduleIndex].content];
		if (!updatedContents[contentIndex]) {
			updatedContents[contentIndex] = {};
		}

		updatedContents[contentIndex] = {
			...updatedContents[contentIndex],
			[name]: value,
		};

		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};
		setModules(updatedModules);
	};

	const handleAddContent = (moduleIndex) => {
		const updatedModules = [...modules];
		const updatedContents = updatedModules[moduleIndex].content || [];
		updatedContents.push({ name: "", description: "" });
		updatedModules[moduleIndex] = {
			...updatedModules[moduleIndex],
			content: updatedContents,
		};
		setModules(updatedModules);
	};

	const handleDeleteContent = (moduleIndex, contentIndex) => {
		const updatedModules = [...modules];
		updatedModules[moduleIndex].content.splice(contentIndex, 1);
		setModules(updatedModules);
	};

	const handleDeleteModule = (moduleIndex) => {
		const updatedModules = [...modules];
		updatedModules.splice(moduleIndex, 1);
		setModules(updatedModules);
	};

	const handleAddModule = () => {
		setModules([...modules, { name: "", description: "", content: [] }]);
	};

	const handleSubmit = (e) => {
		e.preventDefault();

		const course = {
			name: formData.courseName,
			description: formData.description,
			modules: modules.map((module) => ({
				name: module.name,
				description: module.description,
				content: module.content.map((content) => ({
					name: content.name,
					description: content.description,
				})),
			})),
		};

		console.log("course data ", course);
		// onAddCourse(course);

		setModules([
			{ name: "", description: "", content: [{ name: "", description: "" }] },
		]);
		setFormData({ courseName: "", description: "" });
	};

			/* Create Module Section Start */




	return (
		<form onSubmit={handleSubmit} className="container mt-0 module-box box pb-40">
			<h2>Crear Curso:</h2>

			<div className="mb-3">
				<label className="form-label text-large">Nombre:</label>
				<input
					type="text"
					name="courseName"
					value={formData.courseName}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Nombre del curso"
				/>
			</div>

			<div className="mb-3">
				<label className="form-label mt-2">Descripcion:</label>
				<textarea
					name="description"
					value={formData.description}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Describa su curso aqui"
				/>
			</div>

			{/* Create Module Section Start */}
			{modules.length !== 0 && <h3 className="mt-50">Crear modulos:</h3>}

			{modules.map((module, moduleIndex) => (
				<div key={moduleIndex} className="mb-20 boxs px-3 py-4 rounded module-box">
					<div className="container">
						<div className="row">
							<div className="col-md-10">
								<div>
									<label className="form-label mt-2 mb-1">Titulo:</label>
									<input
										type="text"
										name="moduleName"
										value={module.moduleName}
										onChange={(e) => handleModuleChange(moduleIndex, e)}
										className="form-control mb-20"
										placeholder="Titulo del modulo"
									/>

									<label className="form-label mt-2 mb-1">Descripcion:</label>
									<input
										type="text"
										name="moduleDescription"
										value={module.moduleDescription}
										onChange={(e) => handleModuleChange(moduleIndex, e)}
										className="form-control mb-20"
										placeholder="Describa su modulo aqui"
									/>
								</div>
							</div>

							<div className="col-md-2 d-flex align-items-center justify-content-center">
								<div className="d-flex justify-content-center sl-btn">
									<button
										type="button"
										onClick={() => {
											handleDeleteModule(moduleIndex);
										}}
										className="btn btn-del-modul"
									>
										<i className="fa fa-trash"></i>
									</button>
								</div>
							</div>
						</div>
					</div>

					{module.content.length !== 0 && (
						<h3 className="mt-50">Crear contenido:</h3>
					)}

					{module.content.map((content, contentIndex) => (
						<div key={contentIndex} className="mb-20 px-3 py-4 rounded module-shadow">
							<div className="container">
								<div className="row">
									{/* Selector Option Start */}
									<div className="col-md-10">
										<div className="form-group">
											<h5 className="">Seleccione una opcion de creacion de contenido:</h5>
											<select
												className="form-control"
												value={selectedOption}
												onChange={handleChangeSelect}
											>
												<option value="video">Cargar video</option>
												<option value="text">Crear articulo</option>
												<option value="quiz">Crear quiz de preguntas y respuestas</option>
											</select>
										</div>
										{/* Selector Option End */}

										{/* Video Option Start */}
										{selectedOption === "video" && (
											<div>
												<h5 className="mt-50">Cargar video</h5>
												<label className="form-label mt-2 mb-1">Titulo del video:</label>
												<input
													type="text"
													name="contentName"
													value={content.contentName}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del contenido"
												/>

												<label className="form-label mt-2 mb-1">Url:</label>
												<input
													type="text"
													name="contentDescription"
													value={content.contentDescription}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="url del video"
												/>
											</div>
										)}
										{/* Video Option End */}

										{/* Text Option Start */}
										{selectedOption === "text" && (
											<div>
												<h5 className="mt-50">Crear articulo</h5>
												<label className="form-label mt-2 mb-1">Titulo:</label>
												<input
													type="text"
													name="contentName"
													value={content.contentName}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del articulo"
												/>

												<label className="form-label mt-2 mb-1">Articulo:</label>
												<textarea
													type="text"
													name="contentDescription"
													value={content.contentDescription}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Contenido del articulo"
												/>
											</div>
										)}
										{/* Text Option End */}

										{/* Quiz Option Start */}
										{selectedOption === "quiz" && (
											<div>
												<Quiz


												/>
												{/* <h5 className="mt-50">Crear Quiz:</h5> */}

												{/* <label className="form-label mt-2 mb-1">Titulo:</label>
												<input
													type="text"
													name="contentName"
													value={content.contentName}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Nombre del contenido"
												/>

												<label className="form-label mt-2 mb-1">Descripcion:</label>
												<input
													type="text"
													name="contentDescription"
													value={content.contentDescription}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Descripcion del contenido"
												/> */}
											</div>
										)}
										{/* Quiz Option End */}
									</div>

									<div className="col-md-2 d-flex align-items-center justify-content-center">
										<div className="d-flex justify-content-center">
											<button
												type="button"
												onClick={() => {
													handleDeleteContent(moduleIndex);
												}}
												className="btn btn-del-cont "
											>
												<i className="fa fa-trash"></i>
											</button>
										</div>
									</div>
								</div>
							</div>
						</div>
					))}

					<div className="container d-flex justify-content-center">
						<button
							type="button"
							onClick={() => {
								handleAddContent(moduleIndex);
							}}
							className="btn btn-secondary btn-add btn-add-cont me-2 m-10"
						>
							Agregar Contenido
						</button>
					</div>
				</div>
			))}

			<div className="container d-flex justify-content-center">
				<button
					type="button"
					onClick={handleAddModule}
					className="btn btn-secondary btn-add break-words me-2"
				>
					Agregar Modulo
				</button>
			</div>

			<button type="submit" className="btn btn-send w-50 pt-50">
				Enviar formulario
			</button>
		</form>
	);
};

