import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const QuizContent = ({ quizData }) => {
  const [selectedAnswers, setSelectedAnswers] = useState(new Array(quizData.questions.length).fill(null));
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleOptionSelect = (questionIndex, answerIndex) => {
    const newAnswers = [...selectedAnswers];
    newAnswers[questionIndex] = answerIndex === newAnswers[questionIndex] ? null : answerIndex;
    setSelectedAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsSubmitted(true);
  };

  return (
    <div className="content-box quiz-content">
      {/* Render quiz questions and options */}
      {quizData.questions.map((question, questionIndex) => (
        <div key={questionIndex} className="question-container">
          <h3>{question.question}</h3>
          <div className="options-container">
            {question.answers.map((answer, answerIndex) => (
              <div
                key={answerIndex}
                className={`option ${selectedAnswers[questionIndex] === answerIndex ? 'selected' : ''} ${selectedAnswers[questionIndex] === answerIndex && quizData.questions[questionIndex].correctAnswer === answerIndex ? 'correct' : ''} ${selectedAnswers[questionIndex] === answerIndex && quizData.questions[questionIndex].correctAnswer !== answerIndex ? 'incorrect' : ''}`}
                onClick={() => handleOptionSelect(questionIndex, answerIndex)}
              >
                {answer}
              </div>
            ))}
          </div>
        </div>
      ))}
      {/* Submit button */}
      <Button onClick={handleSubmit} disabled={isSubmitted}>Submit</Button>
    </div>
  );
};

export default QuizContent;
