import create from "zustand"

const initialState = {
  course: {
    name: "",
    description: "",
    modules: []
  }
}

const useCourseStore = create((set) => ({
  // State
  course: initialState.course,

  // Actions

  // Actions
  setCourse: (newCourse) => set({ course: newCourse }),

  updateModuleInfo: (moduleIndex, key, value) => {
    set((state) => {
      const updatedModules = [...state.course.modules];
      updatedModules[moduleIndex][key] = value;
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleDeleteModule: (moduleIndex) => {
    set((state) => {
      const updatedModules = state.course.modules.filter((module, idx) => idx !== moduleIndex);
      return { course: { modules: updatedModules } };
    });
  },

  setModuleContent: (moduleIndex, content) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex) {
          return { ...module, content };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  deleteContent: (moduleIndex, contentIndex) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex) {
          const updatedContent = [...module.content];
          updatedContent.splice(contentIndex, 1);
          return { ...module, content: updatedContent };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  addContent: (moduleIndex, contentType) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex) {
          let updatedContent = [...(module.content || [])];

          if (contentType === "video") {
            updatedContent.push({ type: "video", url: "" });
          } else if (contentType === "text") {
            updatedContent.push({ type: "text", text: "" });
          } else if (contentType === "quiz") {
            const existingQuizIndex = updatedContent.findIndex((content) => content.type === "quiz");
            if (existingQuizIndex === -1) {
              const quizData = {
                question: "",
                answers: ["", "", "", ""],
                correctAnswer: "",
              };
              updatedContent.push({ type: "quiz", question: [quizData] });
            }
          }

          return { ...module, content: updatedContent };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleQuestion: (moduleIndex, questionIndex, key, value) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex && module.content[questionIndex].type === "quiz") {
          const updatedQuestion = { ...module.content[questionIndex] };
          updatedQuestion.question = value;
          return {
            ...module,
            content: module.content.map((content, cIdx) => (cIdx === questionIndex ? updatedQuestion : content)),
          };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleAnswerChange: (moduleIndex, questionIndex, answerIndex, value) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex && module.content[questionIndex].type === "quiz") {
          const updatedQuestion = { ...module.content[questionIndex] };
          updatedQuestion.answers[answerIndex] = value;
          return {
            ...module,
            content: module.content.map((content, cIdx) => (cIdx === questionIndex ? updatedQuestion : content)),
          };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleCorrectAnswerChange: (moduleIndex, questionIndex, value) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex && module.content[questionIndex].type === "quiz") {
          const updatedQuestion = { ...module.content[questionIndex] };
          updatedQuestion.correctAnswer = value;
          return {
            ...module,
            content: module.content.map((content, cIdx) => (cIdx === questionIndex ? updatedQuestion : content)),
          };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleAddQuestion: (moduleIndex) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex) {
          const updatedContent = [...(module.content || [])];
          const existingQuizIndex = updatedContent.findIndex((content) => content.type === "quiz");

          if (existingQuizIndex !== -1) {
            const updatedQuestion = { question: "", answers: ["", "", "", ""], correctAnswer: "" };
            updatedContent[existingQuizIndex].question.push(updatedQuestion);
          }

          return { ...module, content: updatedContent };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  },

  handleDeleteQuestion: (moduleIndex, questionIndex) => {
    set((state) => {
      const updatedModules = state.course.modules.map((module, idx) => {
        if (idx === moduleIndex && module.content[questionIndex].type === "quiz") {
          const updatedContent = [...module.content];
          updatedContent[questionIndex].question.splice(questionIndex, 1);
          return { ...module, content: updatedContent };
        }
        return module;
      });
      return { ...state, course: { ...state.course, modules: updatedModules } };
    });
  }

}))

export default useCourseStore;
