import React, { useEffect, useState } from "react";
// import { Link } from "react-router-dom";
import ReactPaginate from "react-paginate";
import Swal from "sweetalert2";
import DataTable from 'react-data-table-component'

const AdminAllCourses = () => {

	const [coursesData, setCoursesData] = useState([])

	const showCoursesData = async () => {
		const response = await fetch(
			`${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_GET_ALL}`
		);
		const data = await response.json();
		console.log(data)
		setCoursesData(data);
	}


	useEffect(() => {
		showCoursesData()
	}, [])


	const columns = [
		{
			name: 'ID',
			selector: row => row.id
		},
	]


	const [fetchData, setFetchData] = useState([]);
	const [currentPage, setCurrentPage] = useState(0);
	const coursesPerPage = 9;

	useEffect(() => {
		const getCourses = async () => {
			try {
				const response = await fetch(
					`${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_GET_ALL}`
				);
				const data = await response.json();
				setFetchData(data);
			} catch (error) {
				console.error("Error fetching courses:", error);
			}
		};

		getCourses();
	}, []);

	const handlePageChange = ({ selected }) => {
		window.scrollTo({
			top: 250,
			behavior: "smooth",
		});
		setCurrentPage(selected);
	};

	const totalCourses = fetchData.length;
	const pageCount = Math.ceil(totalCourses / coursesPerPage);

	const renderCourses = () => {
		const startIndex = currentPage * coursesPerPage;
		const selectedCourses = fetchData.slice(
			startIndex,
			startIndex + coursesPerPage
		);

		return selectedCourses.map((course) => (
			<div
				key={course.id}
				className="course-item row align-items-center d-flex flex-wrap border rounded p-3 mb-3"
			>
				<div className="col-lg-9 col-md-9 col-sm-12 col-xs-12 d-flex align-items-center">
					<p className="course-name text-start m-0">
						<strong>{course.courseData.name}</strong>
					</p>
				</div>

				<div className="col-lg-3 col-md-3 col-sm-12 col-xs-12 d-flex justify-content-end align-items-center">
					<button
						className={`btn-status  ${course.active ? "btn-deactivate" : "btn-activate"
							} me-2`}
						onClick={() => handleActivateCourse(course.active, course.id)}
					>
						{course.active ? (<i className="toggle-off fa fa-power-off"></i>) : (<i className="toggle-on fa fa-power-off"></i>)}
					</button>

					<button
						className="btn2 btn-modify me-2"
						onClick={() => handleModify(course.id)}
					>
						<i className="fa fa-edit"></i>
					</button>

					<button
						className="btn2 btn-delete me-2"
						onClick={() => handleDelete(course.id)}
					>
						<i className="fa fa-trash"></i>
					</button>
				</div>
			</div>
		));
	};

	////////////////////////////
	// MANAGE ACTIVATION
	////////////////////////////
	const handleActivateCourse = async (courseStatus, courseId) => {
		const action = courseStatus ? "Desactivar" : "Activar";

		Swal.fire({
			title: "Confirme Accion",
			text: `Esta seguro que desea ${action} este curso?`,
			icon: "warning",
			showCancelButton: true,
			confirmButtonColor: "#3085d6",
			cancelButtonColor: "#d33",
			confirmButtonText: "Activate",
			cancelButtonText: "Cancel",
			reverseButtons: true,
		}).then((result) => {
			if (result.isConfirmed) {
				handleCourseStatus(courseStatus, courseId);
			}
		});
	};

	const handleCourseStatus = async (courseStatus, courseId) => {
		console.log("Course", courseStatus, courseId);

		if (courseStatus === true) {
			try {
				const response = await fetch(
					`${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_DEACTIVATE}/${courseId}`,
					{
						method: "PUT",
						headers: {
							"Content-Type": "application/json",
						},
					}
				);

				if (response.ok) {
					Swal.fire({
						position: "top-end",
						icon: "success",
						title: "¡Curso desactivado con éxito!",
						text: `El curso ha sido desactivado exitosamente.`,
						showConfirmButton: false,
						timer: 1000,
					}).then(() => {
						window.location.href = "/administrar-todos";
					});
				} else {
					Swal.fire({
						title: "¡Ups! Algo salió mal",
						text: `Lo sentimos, no se pudo desactivar el curso en este momento. Por favor, inténtalo de nuevo más tarde`,
						icon: "error",
					});
				}
			} catch (error) {
				console.log(error);
				Swal.fire({
					title: "¡Error de procesamiento de activacion!",
					text:
						"Parece que ocurrió un error mientras procesábamos tu solicitud. Por favor, inténtalo nuevamente más tarde. Si el problema persiste, ponte en contacto con el equipo de soporte para obtener asistencia adicional.",
					icon: "error",
				});
			}
		} else {
			try {
				const response = await fetch(
					`${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_COURSES_ACTIVATE}/${courseId}`,
					{
						method: "PUT",
						headers: {
							"Content-Type": "application/json",
						},
					}
				);

				if (response.ok) {
					Swal.fire({
						position: "top-end",
						icon: "success",
						title: "¡Curso activado con éxito!",
						text: `El curso ha sido activado exitosamente.`,
						showConfirmButton: false,
						timer: 2500,
					}).then(() => {
						window.location.href = "/administrar-todos";
					});
				} else {
					Swal.fire({
						title: "¡Ups! Algo salió mal",
						text: `Lo sentimos, no se pudo activar el curso en este momento. Por favor, inténtalo de nuevo más tarde`,
						icon: "error",
					});
				}
			} catch (error) {
				Swal.fire({
					title: "¡Error de procesamiento de desactivacion!",
					text:
						"Parece que ocurrió un error mientras procesábamos tu solicitud. Por favor, inténtalo nuevamente más tarde. Si el problema persiste, ponte en contacto con el equipo de soporte para obtener asistencia adicional.",
					icon: "error",
				});
			}
		}
	};

	////////////////////////////
	// COURSE DELETE
	////////////////////////////
	const handleDeleteCourse = async (courseId) => {
		Swal.fire({
			position: "top-end",
			icon: "success",
			title: "¡Curso eliminado con éxito!",
			text: `El curso ha sido eliminado exitosamente.`,
			showConfirmButton: false,
			timer: 2500,
		}).then(() => {
			window.location.href = "/administrar-todos";
		});
	};

	const handleDelete = (courseId) => {
		Swal.fire({
			title: "Confirme Accion",
			text: `Esta seguro que desea eliminar este curso?`,
			icon: "warning",
			showCancelButton: true,
			confirmButtonColor: "#3085d6",
			cancelButtonColor: "#d33",
			confirmButtonText: "Activate",
			cancelButtonText: "Cancel",
			reverseButtons: true,
		}).then((result) => {
			if (result.isConfirmed) {
				handleDeleteCourse(courseId);
			}
		});
	};

	////////////////////////////
	// REDIRECT TO MODIFY
	////////////////////////////
	const handleModify = (id) => {
		window.location.href = `/modify/${id}`;
	};

	return (
		<div>

			<DataTable
				columns={columns}
				data={coursesData}
			/>


		</div>


	)
};
export default AdminAllCourses;




