	////////////////////////////
 const [categories, setCategories] = useState([]);
	const [activeCategory, setActiveCategory] = useState(undefined);
	const [currentPage, setCurrentPage] = useState(0);
	const coursesPerPage = 9;

	let coursesCount = 0; // need this one outside in order to be accesed by the pagination

	const renderCoursePanel = () => {
		let filteredCourses;
		if (activeCategory === undefined) {
			filteredCourses = fetchData;
			coursesCount = filteredCourses.length;
		} else {
			// Filter courses based on active category
			filteredCourses = fetchData.filter((course) =>
				course.category.includes(activeCategory)
			);
			coursesCount = filteredCourses.length;

			console.log("filtered Courses ", filteredCourses);
		}

		const offset = currentPage * coursesPerPage;
		const currentPageCourses = filteredCourses.slice(
			offset,
			offset + coursesPerPage
		);

		console.log("current ", currentPageCourses);

		return (
			<div className="row">
				{currentPageCourses.map((course) => (
					<div key={course.id} className="col-lg-4 col-md-6">
