							courseClass="courses-item md-mb-30"
							courseImg={courseImg1}
							courseTitle={course.name || "Curso en desarrollo"}
							courseDuration={course.duration || "10"}
							courseId={course.id || "12312312"}
							courseCategory={course.category || "En desarrollo"}
						/>
					</div>
				))}
			</div>
		);
	};

	const handlePageChange = ({ selected }) => {
		window.scrollTo({
			top: 250,
			behavior: "smooth",
		});
		setCurrentPage(selected);
	};

