import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const CourseSingleTwo = (props) => {
  const {
    courseClass,
    courseImg,
    courseTitle,
    courseCategory,
    courseDuration,
    courseDetail,
    courseId
  } = props;

  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    // Reset loading state to true when the route parameters change
    setLoading(true);
  }, [location]);

  useEffect(() => {
    // Check if all necessary props are available
    if (
      courseClass &&
      courseImg &&
      courseTitle &&
      courseCategory &&
      courseDuration &&
      courseDetail &&
      courseId
    ) {
      // All necessary props are available, set loading to false
      setLoading(false);
    }
  }, [courseClass, courseImg, courseTitle, courseCategory, courseDuration, courseDetail, courseId]);

  if (loading) {
    // If loading is true, display loading indicator or placeholder content
    return <div>Loading...</div>;
  }

  // Render the component with the provided props
  return (
    <div className={courseClass ? courseClass : "courses-item"}>
      <div className="img-part">
        <img src={courseImg} alt={courseTitle} />
      </div>
      <div className="content-part">
        <h3 className="title">
          <Link to={`/course/course-single/${courseId}`}>
            {courseTitle ? courseTitle : ""}
          </Link>
        </h3>
        <ul className="meta-part no-overflow">
          <li>
            {" "}
            <span className="category">{courseCategory ? courseCategory : ""}</span>
          </li>
        </ul>
        <div className="bottom-part">
          <div className="info-meta">
            <p className="detail">{courseDetail}</p>
          </div>
          <div className="info-meta">
            <ul className="meta-part">
              <li>
                <span className="price">
                  {courseDuration ? courseDuration : "10.00"} hs
                </span>
              </li>
              <li className="btn-part">
                <Link to={`/course/course-single/${courseId}`}>
                  {props.btnText}
                  <i className="flaticon-right-arrow"></i>
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseSingleTwo;
