import React, { useState } from 'react';
import { ListGroup, ListGroupItem, Collapse, Container, Row, Col, Button } from 'react-bootstrap';

const CourseFlow = ({ courseData }) => {
 // State variables
 const [isMenuOpen, setIsMenuOpen] = useState(true);
 const [selectedContent, setSelectedContent] = useState(null);

 // Content click handler
 const handleContentClick = (index) => {
  setSelectedContent(index);
 };

 // Button click handler
 const handleButtonClick = () => {
  setIsMenuOpen(!isMenuOpen);
 };

 return (
  <Container fluid className="course-container outred position-relative">
   <div style={{ position: 'absolute', top: '10px', right: '10px', zIndex: 5 }}>
    <Button variant="dark" onClick={handleButtonClick}>
     {isMenuOpen ? 'Hide Menu' : 'Show Menu'}
    </Button>
   </div>
   <Row>
    {/* Content column with dynamic width */}
    <Col xs={12} md={isMenuOpen ? 9 : 12} className="d-flex flex-column">
     {/* Content box */}
     <div className="content-box">
      {/* Render content based on selectedContent */}
      {selectedContent !== null && (
       <div>
        {/* Render content based on selectedContent */}
        {/* For example: */}
        {/* <h2>{courseData.modules[selectedContent].title}</h2> */}
        {/* Render content items here */}
       </div>
      )}
     </div>
    </Col>

    {/* Menu column, always visible with conditional collapse */}
    {isMenuOpen && (
     <Col xs={12} md={3} className="d-flex flex-column ms-auto relative">
      <Collapse in={isMenuOpen}>
       <ListGroup className='content-menu'>
        {/* Render menu items based on modules */}
        {courseData.modules.map((module, index) => (
         <div key={index}>
          <ListGroupItem key={`${index}-title`} disabled>
           {module.name}
          </ListGroupItem>
          {module.content.map((content, contentIndex) => (
           <ListGroupItem key={`${index}-${contentIndex}`} action onClick={() => handleContentClick(contentIndex)}>
            {content.type === 'quiz' ? 'Quiz' : content.title}
           </ListGroupItem>
          ))}
         </div>
        ))}
       </ListGroup>
      </Collapse>
     </Col>
    )}
   </Row>
  </Container>
 );
};

export default CourseFlow;




