import React from "react";

const AuthGuard = ({ component: Component, isAuthenticated, ...rest }) => {
 console.log("isAuthenticated:", isAuthenticated); // Check the value of isAuthenticated

 if (!isAuthenticated) {
  console.log("Redirecting to /login");
  window.location.href = "/login";
  return null; // Return null to prevent rendering the protected component
 }

 // Render the protected component if authenticated
 return <Component {...rest} />;
};

export default AuthGuard;
