import React, { useState } from 'react';
import { ListGroup, ListGroupItem, Collapse, Container, Row, Col, Button } from 'react-bootstrap';

const CourseFlow = ({ courseData }) => {
  // State variables
  const [openModuleIndex, setOpenModuleIndex] = useState(null);
  const [selectedContent, setSelectedContent] = useState(null);

  // Content click handler
  const handleContentClick = (index) => {
    setSelectedContent(index);
  };

  // Module click handler
  const handleModuleClick = (index) => {
    setOpenModuleIndex(openModuleIndex === index ? null : index);
  };

  return (
    <Container fluid className="course-container outred position-relative">
      <Row>
        {/* Content column with dynamic width */}
        <Col xs={12} md={9} className="d-flex flex-column">
          {/* Content box */}
          <div className="content-box">
            {/* Render content based on selectedContent */}
            {selectedContent !== null && (
              <div>
                {/* Render content based on selectedContent */}
                {/* For example: */}
                {/* <h2>{courseData.modules[openModuleIndex].content[selectedContent].title}</h2> */}
                {/* Render content items here */}
              </div>
            )}
          </div>
        </Col>

        {/* Menu column */}
        <Col xs={12} md={3} className="d-flex flex-column">
          {/* Render menu items based on modules */}
          {courseData.modules.map((module, index) => (
            <div key={index} className="mb-3">
              <Button
                variant="light"
                onClick={() => handleModuleClick(index)}
                aria-controls={`module-${index}-content`}
                aria-expanded={openModuleIndex === index}
                block
              >
                {module.name}
              </Button>
              <Collapse in={openModuleIndex === index}>
                <ListGroup id={`module-${index}-content`} className='content-menu'>
                  {module.content.map((content, contentIndex) => (
                    <ListGroupItem
                      key={`${index}-${contentIndex}`}
                      action
                      onClick={() => handleContentClick(contentIndex)}
                    >
                      {content.type === 'quiz' ? 'Quiz' : content.title}
                    </ListGroupItem>
                  ))}
                </ListGroup>
              </Collapse>
            </div>
          ))}
        </Col>
      </Row>
    </Container>
  );
};

export default CourseFlow;
