import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import CourseSingleTwo from "../../components/Courses/CourseSingleTwo";

// Course courseImg
import courseImg1 from "../../assets/img/courses/1.jpg";
import courseImg2 from "../../assets/img/courses/2.jpg";
import courseImg3 from "../../assets/img/courses/3.jpg";
import courseImg4 from "../../assets/img/courses/4.jpg";
import courseImg5 from "../../assets/img/courses/5.jpg";
import courseImg6 from "../../assets/img/courses/6.jpg";
// import courseImg7 from '../../assets/img/courses/7.jpg';
// import courseImg8 from '../../assets/img/courses/8.jpg';

const CoursePart = () => {
	const fetchData = [
		// Finances
		{
			id: "1",
			name: "Financial Management Fundamentals",
			detail: "Learn the basics of financial management.",
			duration: 8,
			category: "Finances",
		},
		{
			id: "2",
			name: "Investment Strategies",
			detail: "Explore various investment strategies and techniques.",
			duration: 12,
			category: "Finances",
		},
		{
			id: "3",
			name: "Budgeting Essentials",
			detail: "Master the art of budgeting for personal and business finances.",
			duration: 6,
			category: "Finances",
		},
		{
			id: "4",
			name: "Accounting Principles",
			detail: "Understand the fundamental principles of accounting.",
			duration: 10,
			category: "Finances",
		},
		{
			id: "5",
			name: "Taxation Basics",
			detail: "Learn about basic taxation concepts and regulations.",
			duration: 7,
			category: "Finances",
		},
		{
			id: "6",
			name: "Financial Planning",
			detail: "Develop effective financial plans for individuals and businesses.",
			duration: 9,
			category: "Finances",
		},
		// Web development
		{
			id: "7",
			name: "HTML5 and CSS3 Fundamentals",
			detail: "Get started with HTML5 and CSS3 for web development.",
			duration: 8,
			category: "Web development",
		},
		{
			id: "8",
			name: "JavaScript Essentials",
			detail: "Learn the essentials of JavaScript programming.",
			duration: 10,
			category: "Web development",
		},
		{
			id: "9",
			name: "Responsive Web Design",
			detail: "Master the art of creating responsive web designs.",
			duration: 6,
			category: "Web development",
		},
		{
			id: "10",
			name: "Backend Development with Node.js",
			detail: "Explore backend development using Node.js.",
			duration: 12,
			category: "Web development",
		},
		{
			id: "11",
			name: "React.js Basics",
			detail: "Learn the basics of React.js for building user interfaces.",
			duration: 9,
			category: "Web development",
		},
		{
			id: "12",
			name: "Frontend Frameworks Comparison",
			detail: "Compare popular frontend frameworks like React, Angular, and Vue.",
			duration: 7,
			category: "Web development",
		},
		// Trading
		{
			id: "13",
			name: "Introduction to Stock Trading",
			detail: "Get introduced to the world of stock trading.",
			duration: 8,
			category: "Trading",
		},
		{
			id: "14",
			name: "Technical Analysis Basics",
			detail: "Learn the basics of technical analysis for trading.",
			duration: 10,
			category: "Trading",
		},
		{
			id: "15",
			name: "Forex Trading Fundamentals",
			detail: "Understand the fundamentals of forex trading.",
			duration: 6,
			category: "Trading",
		},
		{
			id: "16",
			name: "Options Trading Strategies",
			detail: "Explore various options trading strategies.",
			duration: 12,
			category: "Trading",
		},
		{
			id: "17",
			name: "Cryptocurrency Trading",
			detail: "Learn about trading cryptocurrencies like Bitcoin and Ethereum.",
			duration: 9,
			category: "Trading",
		},
		{
			id: "18",
			name: "Risk Management in Trading",
			detail: "Master the art of risk management in trading.",
			duration: 7,
			category: "Trading",
		},
		// Marketing
		{
			id: "19",
			name: "Digital Marketing Essentials",
			detail: "Learn the essentials of digital marketing.",
			duration: 8,
			category: "Marketing",
		},
		{
			id: "20",
			name: "Social Media Marketing Strategies",
			detail: "Explore effective social media marketing strategies.",
			duration: 10,
			category: "Marketing",
		},
		{
			id: "21",
			name: "Content Marketing Basics",
			detail: "Understand the basics of content marketing.",
			duration: 6,
			category: "Marketing",
		},
		{
			id: "22",
			name: "Email Marketing Mastery",
			detail: "Master the art of email marketing for business growth.",
			duration: 12,
			category: "Marketing",
		},
		{
			id: "23",
			name: "SEO Fundamentals",
			detail: "Learn the fundamentals of search engine optimization (SEO).",
			duration: 9,
			category: "Marketing",
		},
		{
			id: "24",
			name: "Marketing Analytics",
			detail:
				"Understand how to analyze marketing data for better decision-making.",
			duration: 7,
			category: "Marketing",
		},
	];

	const [categories, setCategories] = useState([]);

	const [activeCategory, setActiveCategory] = useState("");

	const renderCoursePanel = (category) => {
		const filteredCourses = fetchData.filter(
			(course) => course.category === category
		);
		return (
			<div>
				{filteredCourses.map((course) => (
					<div key={course.id}>
						<h3>{course.name}</h3>
						<p>{course.detail}</p>
						<p>Duration: {course.duration} hours</p>
					</div>
				))}
			</div>
		);
	};

	////////////////////////////
	// LOAD CATEGORIES
	////////////////////////////
	useEffect(() => {
		const fetchData = async () => {
			try {
				const response = await fetch(
					`${process.env.REACT_APP_ROOT_URL}${process.env.REACT_APP_CATEGORIES_GET_ALL}`
				);
				const data = await response.json();
				const filtered = data.filter((category) => category.active === true);
				setCategories(filtered);
			} catch (error) {
				console.error("Error fetching categories:", error);
			}
		};

		fetchData();
		// eslint-disable-next-line
	}, []);

	const tabStyle = "gridFilter text-center mb-10 md-mb-10";

	return (
		<div
			id="rs-popular-course"
			className="rs-popular-courses style1 orange-style pt-100 pb-100 md-pt-70 md-pb-50"
		>
			<div className="container">
				<Tabs>
					<TabList className={tabStyle}>
						{categories.map((category) => (
							<Tab key={category.id} onClick={() => setActiveCategory(category.name)}>
								<button>{category.name}</button>
							</Tab>
						))}
					</TabList>

					<TabPanel>
						<div className="row ">
							{activeCategory && (
								<div>
									<h2>{activeCategory}</h2>
									{renderCoursePanel(activeCategory)}
								</div>
							)}
						</div>
					</TabPanel>
				</Tabs>
				<div className="pagination-area orange-color text-center mt-60 md-mt-30">
					<ul className="pagination-part">
						<li className="active">
							<Link to="#">1</Link>
						</li>
						<li>
							<Link to="#">2</Link>
						</li>
						<li>
							<Link to="#">
								Next <i className="fa fa-long-arrow-right"></i>
							</Link>
						</li>
					</ul>
				</div>
			</div>
		</div>
	);
};

export default CoursePart;
