import React, { useState } from "react";
import { v4 as uuidv4 } from 'uuid'

const initialFormData = {
	courseName: "",
	description: "",
	modules: []
};

const initialModule = {
	id: "", // Unique identifier for the module
	name: "",
	content: []
};

const initialContent = {
	id: "", // Unique identifier for the content
	type: "",
	title: "",
	url: "",
	text: "",
	questions: []
};

const initialQuestion = {
	id: "", // Unique identifier for the question
	question: "",
	answers: ['', '', '', ''],
	correctAnswer: ""
};

const AddCourseForm = () => {

	const [formData, setFormData] = useState(initialFormData);


	////////////////////////////
	// CHANGE SELECT & RANDOM
	////////////////////////////
	const [selectedOption, setSelectedOption] = useState("video")

	const handleChangeSelect = (e, moduleIndex) => {
		const { value: newOption } = e.target;
		console.log("initial", newOption)


		setFormData((prevFormData) => {
			const updatedModules = [...prevFormData.modules];
			const currentContent = updatedModules[moduleIndex].content.slice(-1)[0]; // Get the last added content

			if (!currentContent) {
				// Handle case when there is no content yet
				return prevFormData;
			}

			switch (newOption) {
				case 'video':
					console.log("video", newOption)

					currentContent.type = 'video';
					currentContent.title = ''; // Reset other properties if needed
					currentContent.url = '';   // Reset other properties if needed
					currentContent.text = '';  // Reset other properties if needed
					currentContent.questions = []; // Reset other properties if needed
					break;
				case 'text':
					console.log("text", newOption)

					currentContent.type = 'text';
					currentContent.title = ''; // Reset other properties if needed
					currentContent.url = '';   // Reset other properties if needed
					currentContent.questions = []; // Reset other properties if needed
					break;
				case 'quiz':
					console.log("quiz", newOption)

					currentContent.type = 'quiz';
					currentContent.title = ''; // Reset other properties if needed
					currentContent.url = '';   // Reset other properties if needed
					currentContent.text = '';  // Reset other properties if needed
					// Optionally, you might want to add initial question(s) here
					currentContent.questions = [initialQuestion];
					break;
				default:
					// Handle default case if needed
					break;
			}

			return {
				...prevFormData,
				modules: updatedModules,
			};
		});
	};


	const generateUniqueId = () => {
		return uuidv4();
	};

	////////////////////////////
	// COURSE NAME, DESCRIPTION
	////////////////////////////
	const handleInputChange = (e) => {
		const { name, value } = e.target;
		setFormData((prevData) => ({ ...prevData, [name]: value }));
	};


	//////////////////////////////////////////////
	// MODULE FUNCTIONS
	//////////////////////////////////////////////
	////////////////////////////
	// ADD MODULE
	const handleAddModule = () => {
		const newModule = {
			id: `module${formData.modules.length + 1}`,
			name: "",
			content: []
		};
		setFormData(prevState => ({
			...prevState,
			modules: [...prevState.modules, newModule]
		}));
	};
	////////////////////////////
	// SET MODULE NAME
	const handleModuleChange = (moduleIndex, e) => {
		const { name, value } = e.target;
		setFormData(prevState => {
			const updatedModules = [...prevState.modules];
			updatedModules[moduleIndex] = {
				...updatedModules[moduleIndex],
				[name]: value
			};
			return { ...prevState, modules: updatedModules };
		});
	};

	////////////////////////////
	// DELETE MODULE
	const handleDeleteModule = (moduleIndex) => {
		setFormData(prevState => {
			const updatedModules = [...prevState.modules];
			updatedModules.splice(moduleIndex, 1);
			return { ...prevState, modules: updatedModules };
		});
	};


	//////////////////////////////////////////////
	// CONTENT FUNCTIONS
	//////////////////////////////////////////////
	////////////////////////////
	// ADD CONTENT
	const handleAddContent = (moduleIndex) => {
		setFormData((prevFormData) => {
			let newContent = {
				...initialContent,
				id: generateUniqueId(),
				type: 'video',
				title: '',
				url: '',
				text: '',
				questions: [],
			};

			const updatedModules = [...prevFormData.modules];
			updatedModules[moduleIndex].content.push(newContent);

			return {
				...prevFormData,
				modules: updatedModules,
			};
		});
	};


	/* 
I can see the logs that are correctly logging data in the console

	updatedContent  Video t title Video t
AddCourseForm.js:192 updatedContent  Video ti title Video ti
AddCourseForm.js:192 updatedContent  Video tit title Video tit
AddCourseForm.js:192 updatedContent  Video titl title Video titl
AddCourseForm.js:192 updatedContent  Video title title Video title
AddCourseForm.js:192 updatedContent  v url v
AddCourseForm.js:192 updatedContent  vi url vi
AddCourseForm.js:192 updatedContent  vid url vid
AddCourseForm.js:192 updatedContent  vide url vide
AddCourseForm.js:192 updatedContent  video url video
AddCourseForm.js:192 updatedContent  video  url video 
AddCourseForm.js:192 updatedContent  video u url video u
AddCourseForm.js:192 updatedContent  video ur url video ur
AddCourseForm.js:192 updatedContent  video url url video url
	*/

	////////////////////////////
	// HANDLE CONTENT CHANGE
	const handleContentChange = (e, moduleIndex, contentIndex) => {
		const { name, value } = e.target;

		setFormData((prevFormData) => {
			const updatedModules = [...prevFormData.modules];
			const updatedModule = { ...updatedModules[moduleIndex] };
			const updatedContent = { ...updatedModule.content[contentIndex] };

			// Update content based on its type
			if (updatedContent.type === "video" || updatedContent.type === "text") {
				// For video and text content, directly update title or URL/text
				updatedContent[name] = value;
				console.log("updatedContent ", updatedContent[name], name, value)
			} else if (updatedContent.type === "quiz") {
				// For quiz content, update the question object
				const updatedQuestion = {
					...updatedContent.questions[0],
					[name]: value,
				};
				updatedContent.questions = [updatedQuestion];
			}

			updatedModule.content[contentIndex] = updatedContent;
			updatedModules[moduleIndex] = updatedModule;

			return { ...prevFormData, modules: updatedModules };
		});
	};


	////////////////////////////
	// SUBMIT FORM
	////////////////////////////
	const handleSubmit = (e) => {
		e.preventDefault();

		setFormData(initialFormData);
	};


	////////////////////////////
	// SHOW DATA
	////////////////////////////
	const [showData, setShowData] = useState(true);

	const formatCourseStructure = () => {
		const formattedData = {
			courseName: formData.courseName,
			description: formData.description,
			modules: formData.modules.map((module) => ({
				id: module.id, // Assuming module id exists in your data
				name: module.name,
				content: module.content.map((content) => ({
					id: content.id, // Assuming content id exists in your data
					type: content.type,
					title: content.title,
					url: content.url,
					text: content.text,
					questions: content.questions.map((question) => ({
						id: question.id, // Assuming question id exists in your data
						question: question.question,
						answers: question.answers,
						correctAnswer: question.correctAnswer
					}))
				}))
			}))
		};

		return JSON.stringify(formattedData, null, 2);
	};


	return (
		<form onSubmit={handleSubmit} className="container mt-0 module-box box pb-40">
			<h2>Crear Curso:</h2>

			<div className="mb-3">
				<label className="form-label text-large">Nombre:</label>
				<input
					type="text"
					name="courseName"
					value={formData.courseName}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Nombre del curso"
				/>
			</div>

			<div className="mb-3">
				<label className="form-label mt-2">Descripcion:</label>
				<textarea
					name="description"
					value={formData.description}
					onChange={handleInputChange}
					className="form-control mb-20"
					placeholder="Describa su curso aqui"
				/>
			</div>



			{/* Create Module Section Start */}
			{formData.modules.length !== 0 && <h3 className="mt-50">Crear modulos:</h3>}

			{formData.modules.map((module, moduleIndex) => (
				<div key={moduleIndex} className="mb-20 boxs px-3 py-4 rounded module-box">
					<div className="container">
						<div className="row">
							<div className="col-md-10">
								<div>
									<label className="form-label mt-2 mb-1">Titulo:</label>
									<input
										type="text"
										name="name"
										value={module.name}
										onChange={(e) => handleModuleChange(moduleIndex, e)}
										className="form-control mb-20"
										placeholder="Titulo del modulo"
									/>

								</div>
							</div>

							<div className="col-md-2 d-flex align-items-center justify-content-center">
								<div className="d-flex justify-content-center sl-btn">
									<button
										type="button"
										onClick={() => {
											handleDeleteModule(moduleIndex);
										}}
										className="btn btn-del-modul"
									>
										<i className="fa fa-trash"></i>
									</button>
								</div>
							</div>
						</div>
					</div>





					{module.content.length !== 0 && (
						<h3 className="mt-50">Crear contenido:</h3>
					)}
					{module.content.map((content, contentIndex) => (
						<div key={contentIndex} className="mb-20 px-3 py-4 rounded module-shadow">
							<div className="container">
								<div className="row">
									{/* Selector Option Start */}
									<div className="col-md-10">
										<div className="form-group">
											<h5 className="">Seleccione una opcion de creacion de contenido:</h5>
											<select
												className="form-control"
												value={content.type}
												onChange={(e) => {
													handleChangeSelect(e, moduleIndex);
												}}
											>
												<option value="video">Cargar video</option>
												<option value="text">Crear articulo</option>
												<option value="quiz">Crear quiz de preguntas y respuestas</option>
											</select>
										</div>
										{/* Selector Option End */}

										{/* Video Option Start */}
										{content.type === "video" && (
											<>
												<h5 className="mt-50">Cargar video</h5>
												<label className="form-label mt-2 mb-1">Titulo del video:</label>
												<input
													type="text"
													name="title"
													value={content.title || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del contenido"
												/>

												<label className="form-label mt-2 mb-1">Url:</label>
												<input
													type="text"
													name="url"
													value={content.url || ""}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="url del video"
												/>
											</>
										)}

										{/* Video Option End */}

										{/* Text Option Start */}
										{content.type === "text" && (
											<div>
												<h5 className="mt-50">Crear articulo</h5>
												<label className="form-label mt-2 mb-1">Titulo:</label>
												<input
													type="text"
													name="title"
													value={content.title}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Titulo del articulo"
												/>

												<label className="form-label mt-2 mb-1">Articulo:</label>
												<textarea
													type="text"
													name="text"
													value={content.text}
													onChange={(e) => handleContentChange(e, moduleIndex, contentIndex)}
													className="form-control mb-20"
													placeholder="Contenido del articulo"
												/>
											</div>
										)}
										{/* Text Option End */}

										{/* Quiz Option Start */}
										{content.type === "quiz" && (
											<div>
												<div className="container mt-4 quiz style1">
													<h5 className="mt-50">Crear Quiz:</h5>

													{content.questions.map((question, questionIndex) => (
														<div key={questionIndex} className="mb-20 rounded module-shadow">
															<div className="container">
																<div className="row contact-widget module-box">
																	<div className="col-md-10  rounded pt-3">
																		<div className="form-group">
																			<label>Ingrese la pregunta o trivia:</label>
																			<textarea
																				name="question"
																				className="form-control"
																				// value={question.question}
																				// onChange={(e) =>
																				// 	// handleQuestion(e, questionIndex)
																				// }
																				placeholder={`Pregunta...`}
																			></textarea>
																		</div>

																		<div className="form-group">
																			<label>Escriba las posibles respuestas:</label>
																			{question.answers.map((answer, answerIndex) => (
																				<div key={answerIndex} className="mb-10">
																					<input
																						name="answer"
																						type="text"
																						className="form-control"
																						value={answer}
																					// onChange={(e) =>
																					// 	// handleAnswerChange(questionIndex, answerIndex, e)
																					// }
																					/>
																				</div>
																			))}

																			<div className="form-group">
																				<label>Seleccione la respuesta correcta:</label>
																				<select
																					className="form-control"
																					value={question.answers.indexOf(question.correct)}
																				// onChange={(e) =>
																				// 	// handleCorrectAnswerChange(e, questionIndex)
																				// }
																				>
																					{question.answers.map((answer, index) => (
																						<option key={index} value={index}>
																							{answer}
																						</option>
																					))}
																				</select>
																			</div>
																		</div>
																	</div>

																	<div className="col-md-2 d-flex align-items-center justify-content-center">
																		<div className="d-flex justify-content-center">
																			<button
																				type="button"
																				onClick={() => {
																					// handleDeleteQuestion(questionIndex);
																				}}
																				className="btn btn-del-cont "
																			>
																				<i className="fa fa-trash"></i>
																			</button>
																		</div>
																	</div>
																</div>
															</div>
														</div>
													))}




													<div className="container d-flex justify-content-center contact-widget">
														<button
															type="button"
															onClick={() => {
																// handleAddQuestion(moduleIndex);
															}}
															className="btn  btn-add btn-add-cont me-2 m-10"
														>
															Agregar Pregunta
														</button>
													</div>

												</div>
											</div>
										)}
										{/* Quiz Option End */}
									</div>

									<div className="col-md-2 d-flex align-items-center justify-content-center">
										<div className="d-flex justify-content-center">
											<button
												type="button"
												onClick={() => {
													// handleDeleteContent(moduleIndex);
												}}
												className="btn btn-del-cont "
											>
												<i className="fa fa-trash"></i>
											</button>
										</div>
									</div>
								</div>
							</div>
						</div>
					))}











					<div className="container d-flex justify-content-center">
						<button
							type="button"
							onClick={() => {
								handleAddContent(moduleIndex);
							}}
							className="btn btn-secondary btn-add btn-add-cont me-2 m-10"
						>
							Agregar Contenido
						</button>
					</div>
				</div>
			))}

			<div className="container d-flex justify-content-center">
				<button
					type="button"
					onClick={handleAddModule}
					className="btn btn-secondary btn-add break-words me-2"
				>
					Agregar Modulo
				</button>
			</div>
			{/* Create Module Section End */}




			<button type="submit" className="btn btn-send w-50 pt-50">
				Enviar formulario
			</button>

			<div className="data-box position-fixed top-0 end-0">
				<button onClick={() => setShowData(!showData)}>Toggle Data</button>
				{showData && (
					<div className="data">
						<pre>{formatCourseStructure()}</pre>
					</div>
				)}
			</div>


		</form>

	);
};

export default AddCourseForm;
