import React, { useState } from 'react';
import { Button } from 'react-bootstrap';

const QuizContent = ({ quizData }) => {
 const [answers, setAnswers] = useState(new Array(quizData.questions.length).fill(null));

 const handleOptionSelect = (questionIndex, answerIndex) => {
  const newAnswers = [...answers];
  newAnswers[questionIndex] = answerIndex === newAnswers[questionIndex] ? null : answerIndex;
  setAnswers(newAnswers);
};
const handleSubmit = () => {
 const result = answers.map((answer, questionIndex) => ({
   answer,
   isCorrect: answer === quizData.questions[questionIndex].correctAnswer
 }));
 setAnswers(result);
};

 return (
  <div className="content-box quiz-content">
   {/* Render quiz questions and options */}
   {quizData.questions.map((question, questionIndex) => (
    <div key={questionIndex} className="question-container">
     <h3>{question.question}</h3>
     <div className="options-container">
      {question.answers.map((answer, answerIndex) => (
     <div
     key={answerIndex}
     className={`option ${answers[questionIndex] === answerIndex ? 'selected' : ''} ${answers[questionIndex] === answerIndex && answers[questionIndex] === quizData.questions[questionIndex].correctAnswer ? 'correct' : ''} ${answers[questionIndex] === answerIndex && answers[questionIndex] !== quizData.questions[questionIndex].correctAnswer ? 'incorrect' : ''}`}
     onClick={() => handleOptionSelect(questionIndex, answerIndex)}
   >
     {answer}
   </div>
      ))}
     </div>
    </div>
   ))}
   {/* Submit button */}
   <Button onClick={handleSubmit}>Submit</Button>
  </div>
 );
};

export default QuizContent;
