import React, { useState } from "react";
import { Helmet } from "react-helmet";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";
import axios from "axios";

import Header from "../components/Layout/Header/Header";
import Footer from "../components/Layout/Footer/Footer";
import Newsletter from "../components/Common/Newsletter";
import ScrollToTop from "../components/Common/ScrollTop";
import OffWrap from "../components/Layout/Header/OffWrap";
import SiteBreadcrumb from "../components/Common/Breadcumb";
import SearchModal from "../components/Layout/Header/SearchModal";

// Image
import favIcon from "../assets/img/fav-orange.png";
import Logo from "../assets/img/logo/dark-logo.png";
import footerLogo from "../assets/img/logo/lite-logo.png";

import bannerbg from "../assets/img/breadcrumbs/2.jpg";

const CustomPlaceholder = () => (
	<div className="d-flex justify-content-center align-items-center">
		<div className="text-center" style={{ minWidth: "100%", height: "55px" }}>
			<div
				className="spinner-grow text-dark "
				role="status"
				style={{ width: "0.5rem", height: "0.5rem" }}
			>
				<span className="sr-only">Loading...</span>
			</div>
			<div
				className="spinner-grow text-dark "
				role="status"
				style={{ width: "0.5rem", height: "0.5rem", marginLeft: "10px" }}
			>
				<span className="sr-only">Loading...</span>
			</div>
			<div
				className="spinner-grow text-dark "
				role="status"
				style={{ width: "0.5rem", height: "0.5rem", marginLeft: "10px" }}
			>
				<span className="sr-only">Loading...</span>
			</div>

			<div
				className="spinner-grow text-dark "
				role="status"
				style={{ width: "0.5rem", height: "0.5rem", marginLeft: "10px" }}
			>
				<span className="sr-only">Loading...</span>
			</div>
			<div
				className="spinner-grow text-dark "
				role="status"
				style={{ width: "0.5rem", height: "0.5rem", marginLeft: "10px" }}
			>
				<span className="sr-only">Loading...</span>
			</div>
		</div>
	</div>
);

const initialRegisterData = {
	firstName: "",
	lastName: "",
	email: "",
	username: "",
	password: "",
	repassword: "",
};

const initialRegisterData2 = {
	firstName: "Andy",
	lastName: "Andy",
	email: "Andy@Andy.com",
	username: "Andy",
	password: "Andy",
	repassword: "Andy",
};

const Register = () => {
	const [loading, setLoading] = useState(false);

	const [registerData, setRegisterData] = useState(initialRegisterData2);

	const handleInputChange = (e) => {
		const { id, value } = e.target;
		setRegisterData((prevData) => ({ ...prevData, [id]: value }));
	};

	const registerAction = async (e) => {
		setLoading(true);

		e.preventDefault();

		console.log(registerData);

		try {
			const response = await axios.post(
				`${process.env.REACT_APP_ROOT_URL}/user`,
				registerData,
				{
					headers: {
						"Content-Type": "application/json",
					},
				}
			);

			if (response.status === 201) {
				console.log("response ok");
				console.log(response);
				Swal.fire({
					title: "Success!",
					text: "Usuario creado exitosamente.",
					icon: "success",
				}).then(() => {
     window.location.href = "/login";
    });
			} else {
				console.log("response wrong");
				console.log(response);
				Swal.fire({
					title: "Error!",
					text: "Failed to create course",
					icon: "error",
				});
			}
		} catch (error) {
			console.log("response error");
			console.log(error);
			Swal.fire({
				title: "Error!",
				text: "Ocurrio un error al crear el usuario",
				icon: "error",
			});
		} finally {
			setLoading(false);
		}
	};

	return (
		<React.Fragment>
			<Helmet>
				<link rel="icon" href={favIcon} />
			</Helmet>
			<OffWrap />
			<Header
				parentMenu="pages"
				secondParentMenu="others"
				headerNormalLogo={Logo}
				headerStickyLogo={Logo}
				CanvasLogo={Logo}
				mobileNormalLogo={Logo}
				CanvasClass="right_menu_togle hidden-md"
				headerClass="full-width-header header-style1 home8-style4"
				TopBar="enable"
				TopBarClass="topbar-area home8-topbar"
				emailAddress="support@website.com"
				Location="374 William S Canning Blvd, MA 2721, USA "
			/>

