import React from "react";
import { Redirect, Route, Switch } from "react-router-dom";

const AuthGuard = ({ component: Component, isAuthenticated, ...rest }) => (
  <Route
    {...rest}
    render={(props) =>
      isAuthenticated ? (
        // Render the protected component directly
        <Component {...props} />
      ) : (
        // Handle unauthenticated users appropriately
        <Redirect to="/login" />
      )
    }
  />
);
